enum wsdf{RIGHT,DOWNLEFT,DOWN,UPRIGHT};
int out[10][10];
int dx[]={ 1,-1, 0, 1};
int dy[]={ 0, 1, 1,-1};
int d,n;

void solv(){
	int i=1;
	int cx=1,cy=0;
	d=RIGHT;
	out[0][0]=1;
	while(i<n*n){
		++i;
		out[cy][cx]=i;		

		if(cy==0 && d==RIGHT)d=DOWNLEFT;
		else if(cy==0 && d==UPRIGHT){
			if(cx==n-1)d=DOWN;
			else d=RIGHT;
		}else if(cx==0 && d==DOWNLEFT){
			if(cy==n-1)d=RIGHT;
			else d=DOWN;
		}else if(cx==0 && d==DOWN)d=UPRIGHT;
		else if(cy==n-1 && d==DOWNLEFT)d=RIGHT;
		else if(cy==n-1 && d==RIGHT)d=UPRIGHT;
		else if(cx==n-1 && d==UPRIGHT)d=DOWN;
		else if(cx==n-1 && d==DOWN)d=DOWNLEFT;

		cx+=dx[d];
		cy+=dy[d];
	}
}

main(){
	int c=0;
	while(cin>>n){
		++c;
		if(n==0)break;
		cout<<"Case "<<c<<":"<<endl;
		solv();
		for(int i=0;i<n;i++){
			for(int j=0;j<n;j++){
				printf("%3d",out[i][j]);
