            if (nx >= N) {
                nx = x;
                ny = y+1;
                dir *= -1;
            }

            if (ny >= N) {
                ny = y;
                nx = x+1;
                dir *= -1;
            }

            if (nx < 0) {
                if (ny >= N) nx++, ny--;
                else nx++;
                dir *= -1;
            }
            if (ny < 0) {
                if (nx >= N) ny++, nx--;
                else ny++;
                dir *= -1;
            }

            x = nx;
            y = ny;
        }

        for (auto i : d) {
            for (auto j : i) cout << " " << j;
            cout << endl;
        }
        count++;
    }

