using namespace std;
int main()
{
	for (int T=1, n; ~scanf("%d", &n), n; ++T) {
		vector<vector<int> > a(n, vector<int>(n, 0));
		int r = 0, c = 0, k = 1;
		a[r][c++] = k++;
		while (!a[n-1][0]) {
			while (0<=r && r<n && 0<=c && c<n) a[r++][c--] = k++;
			if (c < 0) c = 0;
			while (0<=r && r<n && 0<=c && c<n) a[r--][c++] = k++;
			if (r < 0) r = 0;
		}
		if (n % 2 == 0) {
			r--; c++;
			while (!a[n-1][n-1]) {
				while (0<=r && r<n && 0<=c && c<n) a[r--][c++] = k++;
				r+=2, c--;
				while (0<=r && r<n && 0<=c && c<n) a[r++][c--] = k++;
				r--, c+=2;
			}
		}
		else {
			r++; c--;
			while (!a[n-1][n-1]) {
				while (0<=r && r<n && 0<=c && c<n) a[r++][c--] = k++;
				r--, c+=2;
				while (0<=r && r<n && 0<=c && c<n) a[r--][c++] = k++;
				r+=2, c--;
			}
		}

		printf("Case %d:\n", T);
		rep(i, n) {
			rep(j, n) printf("%3d", a[i][j]);
			puts("");
		}
	}
	return 0;
}

