void map_print(int N){
  int j=0; int k=0; int count = 1;
  int map_[N][N];

  // 左上半分の数字埋め
  for(int i=0;i<N;i++){
    int var_i = i;
    if(i%2){
      for(int k=0;k<=i;k++){
	map_[k][i-k] = count++;
      }
    }
    else{
      for(int k=0;k<=i;k++){
	map_[i-k][k] = count++;
      }
    }
  }

  //右下半分残りの数字埋め
  for(int i=0;i<N;i++){
    for(int j=0;j<N-i-1;j++){
      map_[N-i-1][N-j-1] = N*N - map_[i][j] + 1;
    }
  }


  for(int i=0;i<N;i++){
    for(int l=0;l<N;l++){
      cout << setw(3) << right << map_[i][l];
    }
    cout << endl;
  }

}

