int maze[10][10];
void MakeMaze(int n){
  memset(maze,0,sizeof(maze));
  maze[0][0] = 1;
  bool ret = false;
  int x = 1 ;
  int y = -1;
  int line = 1;
  int cnt = 1;
  for(int i = 0 ; i < 2 * (n - 1) ; i ++){
    if(i % 2){
      //odd
      if(i < n - 1){
	//right low
	x = 0;
	//cout<<"rl"<<x<<"\t"<<y<<endl;
      }else{
	//right mid
	x += 2;
	y --;
	//cout<<"rm"<<x<<"\t"<<y<<endl;
      }
      while(((0 <= x && x < n ) && (0 <= y && y < n ))){
	//cout<<"odd\t"<<i<<"\t"<<x<<"\t"<<y<<endl;
	maze[x][y] = ++cnt;
	x++;y--;
      } 
    }else{
      //even
      if(i < n - 1){
	//top low
	y = 0;
	//cout<<"tl"<<x<<"\t"<<y<<endl;
      }else{
	//top mid

	y +=2;
	x --;
	//cout<<"tm"<<x<<"\t"<<y<<endl;
      }
      while(((0 <= x && x < n ) && (0 <= y && y < n ))){
	//cout<<"even\t"<<n<<" "<<i<<"\t"<<x<<"\t"<<y<<endl;
	maze[x][y] = ++cnt;
	x--;y++;
      }
    }
  }

}

int main(void){
  int n,cnt = 0;
  while(cin>>n,n){

    cout<<"Case "<<++cnt<<":"<<endl;
    MakeMaze(n);
    rep(i,n){
      rep(j,n){
	printf("%3d",maze[j][i]);
      }
      cout<<endl;
    }
  }
}

