void move( int count , int ny , int  nx ){

	map[ny][nx] = count ;
	count++;

	if( ny == n-1 && nx == n-1 ){}
	else {
		if( ny+dy[m%2] >= 0 && ny+dy[m%2] <= n-1 &&
			nx+dx[m%2] >= 0 && nx+dx[m%2] <= n-1 )
				move( count , ny+dy[m%2] , nx+dx[m%2] ) ;
		else {
			m++;
