int h[20][20];
int dx[]{ 1,-1 }, dy[]{ -1,1 };
int main() {
	int c = 1, n;
	while (scanf("%d", &n), n) {
		int d = 1; bool idx = 0;
		h[0][0] = 1;
		int px = 0, py = 0;
		while (px != n - 1 || py != n - 1) {
			if (px == 0 || py == 0 || px == n - 1 || py == n - 1) {
				if (px == 0)py++;
				else if (py == 0) {
					if (px < n - 1)px++;
				}
				else if (px == n - 1)py++;
				else if (py == n - 1)px++;
				h[px][py] = ++d;
			}
			int nx = px + dx[idx], ny = py + dy[idx];
			if (0 <= nx&&nx < n && 0 <= ny&&ny < n) {
				px = nx; py = ny;
				h[px][py] = ++d; continue;
			}
			idx = !idx;
			px += dx[idx]; py += dy[idx];
			h[px][py] = ++d;
		}
		printf("Case %d:\n", c);
		rep(i, n){
			rep(j, n)printf("%3d", h[i][j]);
			printf("\n");
		}
		c++;
	}
}

#include <iostream>
#include <cstdio>
#include <vector>
#include <algorithm>

using namespace std;


void solve()
{
	int caseCount = 1;
	int n;
	while (cin >> n, n)
	{
		cout << "Case " << caseCount << ":" << endl;
		vector< vector<int> > field(n, vector<int>(n));
		int cnt = 1;
		field[0][0] = cnt;
		++cnt;
		int x = 0;
		int y = 0;
		int dx = 1;
		int dy = -1;
		while (!(x == n - 1 && y == n - 1))
		{
			if (n <= x + dx)
			{ 
				++y; 
				field[y][x] = cnt; 
				++cnt;
				dx = -1; 
				dy = 1; 
				continue; 
			}
			else if (n <= y + dy)
			{ 
				++x; 
				field[y][x] = cnt;
				++cnt;
				dx = 1; dy = -1; 
				continue; 
			}
			else if (y + dy < 0)
			{ 
				++x; 
				field[y][x] = cnt;
				++cnt;
				dx = -1; 
				dy = 1; 
				continue; 
			}
			else if (x + dx < 0)
			{ 
				++y; 
				field[y][x] = cnt;
				++cnt;
				dx = 1; 
				dy = -1; 
				continue; 
			}
			x += dx;
			y += dy;
			field[y][x] = cnt;
			++cnt;
		}
		for (int i = 0; i < n; ++i)
		{
			for (int j = 0; j < n; ++j)
			{
				printf("%3d", field[i][j]);
			}
			cout << endl;
		}
		++caseCount;
	}
}

int main()
{
	solve();
	return(0);
}

