		int width = 0;
		int height = 0;
		int count = 2;
		Vec[0][0] = 1;
		for(int i = 1; i < n; ++i)
		{
			if(i % 2 == 1)
			{
				width = pos;
				height = 0;
				for(int j = 0; j < pos + 1; ++j)
				{

					Vec[height][width] = count;
					width--;
					height++;
					count++;
				}
				++pos;
			}
			else
			{
				height = pos;
				width = 0;
				for(int j = 0; j < pos + 1; ++j)
				{
					Vec[height][width] = count;
					height--;
					width++;
					count++;
				}
				++pos;
			}
		}
		pos = 1;
		int size = n - 1;
		int a = n;
		for(int i = 0; i < n - 1; ++i)
		{
			if(a % 2 == 0)
			{
				width = pos;
				height = n - 1;
				for(int j = 0; j < size; ++j)
				{

					Vec[height][width] = count;
					width++;
					height--;
					count++;
				}
				++pos;
				size--;
				--a;
			}
			else
			{
				height = pos;
				width = n - 1;
				for(int j = 0; j < size; ++j)
				{
					Vec[height][width] = count;
					height++;
					width--;
					count++;
				}
				++pos;
				size--;
				--a;
			}
		}
