using namespace std;
typedef pair<int, int> P;
static const int INF = 9999999;


void solve()
{
	int step = 1;
	int N;
	while(cin >> N, N)
	{
		cout << "Case " << step << ":" << endl;
		++step;
		vector< vector<int> > Vec(N + 2, vector<int>(N + 2));
		for(int i = 0; i < N + 2; ++i)
		{
			Vec[0][i] = INF;
			Vec[N + 1][i] = INF;
			Vec[i][0] = INF;
			Vec[i][N + 1] = INF;
		}
		int count = 0;
		int num = 1;
		Vec[1][1] = num;
		P p(2, 1);
		bool IsOdd = true;
		while(count != N)
		{
			while(Vec[p.second][p.first] != INF)
			{
				++num;
				Vec[p.second][p.first] = num;
				if(IsOdd)
				{
					p.first -= 1;
					p.second += 1;
				}
				else
				{
					p.first += 1;
					p.second -= 1;
				}
			}
			//FieldàÉß·
			if(IsOdd)
			{
				p.first += 1;
			}
			else
			{
				p.second += 1;
			}
			++count;
			IsOdd = !IsOdd;
		}
		if(N % 2)
		{
			p.first -= 2;
			++p.second;
		}
		else
		{
			++p.first;
			p.second -= 2;
		}
		while(count != 0)
		{
			while(Vec[p.second][p.first] != INF)
			{
				++num;
				Vec[p.second][p.first] = num;
				if(IsOdd)
				{
					p.first += 1;
					p.second -= 1;
				}
				else
				{
					p.first -= 1;
					p.second += 1;
				}
			}
			//FieldàÉß·
			if(IsOdd)
			{
				p.first -= 1;
				p.second += 2;
			}
			else
			{
				p.first += 2;
				p.second -= 1;
			}
			--count;
			IsOdd = !IsOdd;
		}
		for(int i = 1; i < N + 1; ++i)
		{
			for(int j = 1; j < N + 1; ++j)
			{
				printf("%3d", Vec[i][j]);
			}
			cout << endl;
		}
	}
}

int main()
{
	solve();
	return(0);
}

