void moveNextPosition(int *x, int *y, int n){
	// - - 3
	// - * 5
	// 6 7 - 
	static int state = 0;


	switch(state){
		case 3: //右上
			--*x;
			++*y;
			if(!*x) state=5;	// →
			if(*y==n-1) state=7; //↓
			break;
		case 5: //右
			++*y;
			if(!*x) state=6; //左下
			if(*x==n-1) state=3; //右上
			if(*x==*y && *x==n-1) state=0;
			break;
		case 6: // 左下
			++*x;
			--*y;
			if(!*y) state=7; // ↓
			if(*x==n-1) state=5; // →
			break;
		case 7: //下
			++*x;
			if(!*y)state=3; //右上
			if(*y==n-1) state=6;
			if(*x==*y && *x==n-1) state=0;
			break;
		default:
			//initial state
			state=5;
			break;
	}
}

