		if(!out(cx + dx[(dir + 1) % 4], cy + dy[(dir + 1) % 4], n)) {
		  cdir = (dir + 1) % 4;
		}
		else {
		  cdir = (dir + 3) % 4;
		}
		dir ^= 2;
	  }

	  cx += dx[cdir];
	  cy += dy[cdir];
	}
