#include<iostream>

enum ePhase{Right = 0,DownLeft,Down,UpRight,PhaseMax};
using namespace std;
#define MAX 12

void downLeft(int N, int m[][MAX], int &i, int &j){
  int t = m[i][j];
  do{
    m[++i][--j] = ++t;
  }while( j > 1 && i < N );
}
void upRight(int N, int m[][MAX], int &i, int &j){
  int t = m[i][j];
  do{
    m[--i][++j] = ++t;
  }while( i > 1 && j < N );
}

int main(){
  int map[MAX][MAX];
  while( true ){
    int N;
    int i, j;
    int progress = 1;
    int phase = Right;

    cin >> N;
    if( N == 0 ) break;

    i = 1; j = 1;
    map[i][j] = 1;
    while( progress <= N * N ){
      switch( phase ){
      case Right:
	++j;
	if( j > N ){
	  //phase = Down;
	  --j;
	  ++i;
	}
	break;
      case DownLeft:
	downLeft( N, map, i, j );
	progress = map[i-1][j+1];
	break;
      case Down:
	++i;
	if( i > N ){
	  //phase = Right;
	  --i;
	  ++j;
	}
	break;
      case UpRight:
	upRight( N, map, i, j );
	progress = map[i+1][j-1];
	break;
      }
      phase = ( phase + 1 ) % PhaseMax;
      map[i][j] = ++progress;
    }

    for(int i = 1; i <= N; ++i){
      cout << map[i][1];
      for(int j = 2; j <= N; ++j){
	cout << ' ' << map[i][j];
      }
      cout << '\n';
    }
  }
  return 0;
}

