using namespace std;

void JpegCompression(int n, int** arr)
{
	int currN=1;
	vector<int> *diagonal=new vector<int>[2*n -1];

	for(int i=0; i<2*n-1; i++)
	{
		int elemN;
		if(i+1 <= n)elemN=i+1;
		else        elemN=2*n-(i+1);

		for(int j=0; j<elemN; j++)
			diagonal[i].push_back(currN++);

		if(i%2 == 1)
			reverse(diagonal[i].begin(), diagonal[i].end());
	}

	for(int y=0; y<n; y++)
	{
		for(int x=0; x<n; x++)
		{
			arr[x][y] = diagonal[x+y].back();
			diagonal[x+y].pop_back();
		}
	}
	delete[] diagonal;
}

int main(void)
{
	int n, nCase=1;
	while(1)
	{
		cin >> n;
		if(n == 0)break;

		int** ppArr=new int*[n];
		for(int i=0; i<n; i++)
			ppArr[i] = new int[n];
		JpegCompression(n, ppArr);

		cout << "Case " << nCase++ << ':' << endl;
		for(int y=0; y<n; y++)
		{
			cout.width(3);
			cout << ppArr[0][y];
			for(int x=1; x<n; x++)
			{
				cout.width(3);
				cout << ppArr[x][y];
			}
			cout << endl;
		}
		for(int i=0; i<n; i++)
			delete [] ppArr[i];
		delete[] ppArr;
	}

	return 0;
}

