using namespace std;

typedef struct{
  int x;
  int y;
}Point;

const int MAX = 10;
int data[MAX][MAX];

void make(int in){
  data[in-1][in-1] = in*in;
  if(in == 1) return;
  int write = 0,status = 0,come = 0;
  Point now;
  now.y = now.x = 0;
  while(1){
    write++;
    data[now.y][now.x] = write;
    if(write == in*in-1) return;
    if(now.y == 0 && !come){
      if(now.x == in-1) now.y++;
      else now.x++;
      status = 0;
      come = 1;
      continue;
    }
    if(now.x == 0 && !come){
      if(now.y == in-1) now.x++;
      else now.y++;
      status = 1;
      come = 1;
      continue;
    }
    if(now.x == in-1 && !come){
      now.y++;
      status = 0;
      come = 1;
      continue;
    }
    if(now.y == in-1 && !come){
      now.x++;
      status = 1;
      come = 1;
      continue;
    }

    if(status){
      now.x++;
      now.y--;
    }else{
      now.y++;
      now.x--;
    }
    come = 0;
  }
}

int main(){
  int in,rev = 0;
  while(cin >> in && in){
    make(in);
    rev++;
    cout << "Case " << rev << ":" << endl;
    for(int i = 0; i < in; i++){
      for(int j = 0; j < in; j++){
	if(j) cout << " ";
	printf("%3d",data[i][j]);
      }
      cout << endl;
    }
  }
  return 0;
}

