bool check(int x,int y,int n){
    if(x>=0&&x<n&&y>=0&&y<n)
        return 1;
    else
        return 0;
}

int main(){
    int a[10][10];
    int n;
    int dir[2][2]={{-1,1},{1,-1}};
    while(1){
        cas++;
        cin>>n;
        if(!n)
            break;
        int now[2]={0,0};
        int d=0;
        int put = 1;
        a[0][0]=1;
