            show(a,1);
            continue;
        }
        a[0][1]=2;
        while(check(now[0],now[1],n)){
            a[now[0]][now[1]]=put;
            if(!check(now[0]+dir[d][0],now[1]+dir[d][1],n)){
                if(!d){
                    if(now[1]+1<n){
                        now[1]++;
                    }else{
                        now[0]++;
                    }
                }else{
                    if(now[0]+1<n){
                        now[0]++;
                    }else{
                        now[1]++;
                    }

                }
                d=1-d;
            }else{
                now[0]+=dir[d][0];
                now[1]+=dir[d][1];
            }
            put++;
        }
        show(a,n);
