int map[10][10];
int count, p, n;

void tansaku(int,int);

main(){
  int i, j, c;

  for(c=0;;c++){
    scanf("%d", &n);
    if(n==0) break;

    count=p=0;
    tansaku(0,0);

    printf("Case %d:\n", c+1);

    for(i=0;i<n;i++){
      for(j=0;j<n;j++){
	printf("%3d", map[i][j]);
      }
      printf("\n");
    }
  }
  return 0;
}

void tansaku(int y,int x){
  int i, j;

  count++;
  map[y][x]=count;
  if(x==n-1 && y==n-1) return;

  if(x==0 && y==0) p=0;
  else if(x==n-1 && p==3) p=2;
  else if(y==n-1 && p==1) p=0;
  else if(x==n-1 && p==2) p=1;
  else if(y==n-1 && p==0) p=3;
  else if(y==0 &&p==0) p=1;
  else if(x==0 && p==1) p=2;
  else if(x==0 && p==2) p=3;
  else if(y==0 && p==3) p=0;

  if(p==0) tansaku(y,x+1);
  else if(p==1) tansaku(y+1,x-1);
  else if(p==2) tansaku(y+1,x);
  else if(p==3) tansaku(y-1,x+1);

}

