	int n, i, j, x, y, tmp, t = 1;
	int *array = (int *)calloc(100, sizeof(int));

	while(1){
		scanf("%d", &n);
		if(n == 0){
			break;
		}

		x = j = array[0] = 1;
		tmp = 2;
		y = 0;
		i = -1;
		while(tmp <= n*n){
			array[y*n+x] = tmp++;
			x += i;
			y += j;

			if(x < 0 || y < 0 || y >= n || x >= n){
				x -= i;
				y -= j;
				i = -i;
				j = -j;

				if((y == 0 || y == n-1)&& x < n-1){
					x++;
				} else {
					y++;
				}
			}
		}

		printf("Case %d:\n", t++);
		for(i = 0; i < n; i++){
			for(j = 0; j < n; j++){
				printf("%3d", array[i*n+j]);
			}
			printf("\n");
		}		
	}

	return 0;
}

