import java.util.Scanner;

public class Main {

	public static void main(String[] args) {
		Scanner sc = new Scanner(System.in);

		int c_count = 1;
		while (true) {
			final int N = sc.nextInt();

			if (N == 0) {
				break;
			}

			int[][] map = new int[N][N];

			int count = 1;
			int x = 0, y = 0;
			int state = 0;
			map[0][0] = 1;
			count++;
			if (N != 1) {
				x++;

				while (count <= N * N) {


					if (state == 0) {
						for (; x >= 0 && y < N; x--, y++) {
							map[y][x] = count;
							count++;
						}
						x++;
						y--;


						if (y == N - 1) {
							x++;
						} else {
							y++;
						}
						state = 1;

					} else if (state == 1) {
						for (; y >= 0 && x < N; y--, x++) {
							map[y][x] = count;
							count++;
						}
						y++;
						x--;

						if (x == N - 1) {
							y++;
						} else {
							x++;
						}
						state = 0;
					}
				}
			}
			System.out.println("Case " + c_count + ":");
			for (int i = 0; i < N; i++) {
				for (int j = 0; j < N; j++) {
					System.out.printf("%3d", map[i][j]);
				}
				System.out.println();
			}

			c_count++;
		}

	}

}

import java.util.*;

class Main {

    static void solve (int n) {
        int[][] jpeg = new int [n][n];
        int x = 0;
        int y = 0;
        boolean ascending = true;

        for (int i = 1; i <= n * n; i++) {
            jpeg[x][y] = i;
            if ( ascending && y == 0 && x != n-1 )  {
                x += 1;
                ascending = false;
            } else if ( (!ascending) && x == 0 && y != n-1) {
                y += 1;
                ascending = true;
            } else if ( ascending && x == n-1) {
                y += 1;
                ascending = false;
            } else if ( (!ascending) && y == n-1) {
                x += 1;
                ascending = true;
            } else if (ascending) {
                x += 1;
                y -= 1;
            } else {
                x -= 1;
                y += 1;
            }
        }

        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                System.out.printf("%3d", jpeg[j][i]);
            }
            System.out.println("");
        }
    }


    public static void main (String [] args) {

        Scanner sc = new Scanner(System.in);
        int times = 1;

        while (true) {
            int n = sc.nextInt();
            if (n == 0) break;

            System.out.printf("Case %d:\n", times);
            solve(n);
            times += 1;
        }

    }
}

import java.util.Scanner;

public class Main {

	static Scanner sc = new Scanner(System.in);

	public static void main(String[] args) throws Exception {
		int t = 1;
		while (true) {
			int N = sc.nextInt();
			if (N == 0) break;
			int[][] ans = new int[N][N];
			int ord = 1;
			for (int i = 0; i < N; ++i) {
				int r = (i % 2 == 0 ? i : 0);
				int c = (i % 2 == 0 ? 0 : i);
				int dr = (i % 2 == 0 ? -1 : 1);
				int dc = (i % 2 == 0 ? 1 : -1);
				for (int j = 0; j <= i; ++j) {
					ans[r][c] = ord;
					++ord;
					r += dr;
					c += dc;
				}
			}
			System.out.println("Case " + t + ":");
			for (int i = 0; i < N; ++i) {
				for (int j = 0; j < N; ++j) {
					System.out.printf("%3d", i + j < N ? ans[i][j] : N * N + 1 - ans[N - 1 - i][N - 1 - j]);
				}
				System.out.println();
			}
			++t;
		}
	}
}

