                    d = 2;
                    nx = x + dx[d];
                    ny = y + dy[d];
                    d = 1;
                } else if (ny < 0) {
                    d = 0;
                    nx = x + dx[d];
                    ny = y + dy[d];
                    d = 1;
                } else if (ny >= n) {
                    d = 0;
                    nx = x + dx[d];
                    ny = y + dy[d];
                    d = 3;
                } else if (nx < 0) {
                    d = 2;
                    nx = x + dx[d];
                    ny = y + dy[d];
                    d = 3;
