import java.util.Scanner;

public class Main {
	public static void main(String[] args) {
		Scanner in = new Scanner(System.in);
		int n;
		int case_ = 1;
		int[][] jpg;
		final String FORMAT = "%3d";
		while ((n = in.nextInt()) != 0) {
			jpg = new int[n][n];
			System.out.println("Case " + case_++ + ":");
			int num = 1;
			for (int col = 0; col < n; col++) {
				jpg[0][col] = num;
				if (col % 2 == 1) {
					num += 2 * (col + 1);
				} else {
					num++;
				}
			}
			for (int row = 1; row < n; row++) {
				boolean isUp = (row % 2 != 0);
				for (int col = 0; col < n; col++) {
					if (col != n - 1) {
						num = jpg[row - 1][col + 1];
						num += ((isUp) ? +1 : -1);
					} else {
						num = jpg[row - 1][col];
						num += (isUp) ? +1 : ((n - row) * 2);
					}
					jpg[row][col] = num;
					isUp = !isUp;
				}
			}
			for (int[] row : jpg) {
				for (int col : row) {
					System.out.print(String.format(FORMAT, col));
				}
				System.out.println();
			}
		}
	}
}

import java.util.Scanner;

public class Main {
    private static int C = 1;
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        for (int n = sc.nextInt(); n != 0; n = sc.nextInt())
            solve(n);
    }

    private static void solve(int n) {
        int[][] ns = new int[n][n];
        int inc = 1;
        for (int i = 0; i < n; i++) {
            if (i % 2 == 0) {
                for (int j = i; j >= 0; j--) {
                    ns[j][i-j] = inc++;
                }
            } else {
                for (int j = 0; j <= i; j++) {
                    ns[j][i-j] = inc++;
                }
            }
        }

        for (int i = n; i < 2 * (n - 1) + 1; i++) {
            if (i % 2 == 1) {
                for (int j = i - n + 1; j < n; j++) {
                    ns[j][i - j] = inc++;
                }
            } else {
                for (int j = n - 1; j >= i - n + 1 ; j--) {
                    ns[j][i - j] = inc++;
                }
            }
        }

        System.out.printf("Case %d:\n", C++);
        for (int[] as : ns) {
            for (int a : as) {
                System.out.printf("%3d ", a);
            }
            System.out.println();
        }
    }
}

