				if(n == 0) return;
				c++;

				matrix = new int[n][n];
				i = 0; j = 0; m = 1;
				while(i < n-1 || j < n-1){
					migiue();
					if(j < n-1){
						j++;
					}else{
						i++;
					}

					hidarishita();
					if(i < n-1){
						i++;
					}else{
						j++;
					}
				}
				matrix[n-1][n-1] = m;
				print(matrix);
