class Evaluator {
public:
    static int num(string &s, int &k) {
        int a = 0;
        while (isdigit(s[k])) {
            a = a * 10 + (s[k] - '0'); ++k;
        }
        return a;
    }
    static int mul(string &s, int &k) {
        int a = num(s, k);
        while (k < (int)s.size()) {
            if (s[k] == '*') a *= mul(s, ++k);
            else if (s[k] == '/') {
                int b = mul(s, ++k);
                if (b == 0) throw 0; // DivisionByZero
                else if (a % b != 0) throw 1; // LeavingRemaindar
                a /= b;
            }
            else break;
        }
        return a;
    }
    static int add(string &s, int &k) {
        int a = mul(s, k);
        while (k < (int)s.size()) {
            if (s[k] == '+') a += mul(s, ++k);
            else if (s[k] == '-') a -= mul(s, ++k);
            else break;
        }
        return a;
    }
    static bool eq(string &s, int &k) {
        int a = add(s, k);
        if (k < (int)s.size() && s[k] == '=') {
            int b = add(s, ++k);
            if (k < (int)s.size()) return false;
            return a == b;
        }
        return false;
    }
    static bool run(string s) {
        int k; bool ret;
        try {
            ret = eq(s, k=0);
        } catch (int) {
            ret = false;
        }
        return ret;
    }
};

bool issymbol(char c) {
    return c=='+' || c=='-' || c=='*' || c=='/' || c=='=';
}

bool ok(int x, int y, char c) {
    bool ver = true, hor = true;
    if (pz[y-1][x] == '#' && pz[y+1][x] == '#') ver = false;
    if (pz[y][x-1] == '#' && pz[y][x+1] == '#') hor = false;
    if (issymbol(c)) {
        if (pz[y-1][x] == '#' || pz[y+1][x] == '#' || issymbol(pz[y-1][x]) || issymbol(pz[y+1][x])) ver = false;
        if (pz[y][x-1] == '#' || pz[y][x+1] == '#' || issymbol(pz[y][x-1]) || issymbol(pz[y][x+1])) hor = false;
        if ( ! ver && ! hor) return false;
    } else if (c == '0') {
        if (pz[y-1][x] == '/' || pz[y][x-1] == '/' || isdigit(pz[y+1][x]) || isdigit(pz[y][x+1])) return false;
    } else {
        if (pz[y-1][x] == '0' || pz[y][x-1] == '0') return false;
    }
    int tx, ty; string s; bool blank;
    if (ver) {
        blank=false; s=c; ty=y-1;
        while (pz[ty][x] != '#') {
            s=pz[ty][x]+s;
            blank|=(pz[ty][x]=='.');
            --ty;
        }
        ty=y+1;
        while (pz[ty][x] != '#') {
            s+=pz[ty][x];
            blank|=(pz[ty][x]=='.');
            ++ty;
        }
        if (1<(int)s.size() && ! blank && ! Evaluator::run(s)) return false;
    }
    if (hor) {
        blank=false; s=c; tx=x-1;
        while (pz[y][tx] != '#') {
            s=pz[y][tx]+s;
            blank|=(pz[y][tx]=='.');
            --tx;
        }
        tx=x+1;
        while (pz[y][tx] != '#') {
            s+=pz[y][tx];
            blank|=(pz[y][tx]=='.');
            ++tx;
        }
        if (1<(int)s.size() && ! blank && ! Evaluator::run(s)) return false;
    }
    return true;
}

string solve(int k, int si) {
    if (n <= k) return "Yes";
    for (int i=si; i<n; ++i) {
        if ( ! used[i] && ok(bx[i], by[i], ch[k])) {
            used[i] = true;
            pz[by[i]][bx[i]] = ch[k];
            string res = solve(k+1, (n<=k+1 || ch[k+1]==ch[k]) ? i+1 : 0);
            used[i] = false;
            pz[by[i]][bx[i]] = '.';
            if (res == "Yes") return res;
        }
    }
    return "No";
}

bool cmb(const char& l, const char& r) {
    if (l == '=') return true;
    if (r == '=') return false;
    return l < r;
}

