			if (!flag) break;
		}
		if (flag) return true;
	}
	//} while (next_permutation(symbol, symbol + n));

	return false;
}

int main() {
	while (cin >> H >> W, H) {
		puts(solve() ? "Yes" : "No");
	}
}

