    bool f = true;
    rep(i,h)rep(j,w){
      if(j==0 || g[i][j-1]=='#'){
	string tmp;
	rep(k,w){
	  if(g[i][k] == '#')break;
	  tmp += g[i][k];
	}
	if(tmp.size()>=3 && tmp.find('.')==string::npos && !check(tmp))f = false;
      }

      if(i==0 || g[i-1][j]=='#'){
	string tmp;
	rep(k,h){
	  if(g[k][j] == '#')break;
	  tmp += g[k][j];
	}
	if(tmp.size()>=3 && tmp.find('.')==string::npos && !check(tmp))f = false;
      }
    }

    if(!f){
      cout << "No" << endl;
      continue;
    }

    memset(l,-1,sizeof(l)); memset(r,-1,sizeof(r));
    memset(u,-1,sizeof(u)); memset(d,-1,sizeof(d));
    rep(i,h)rep(j,w){
      if(g[i][j] == '.'){
	int cnt = 0;
	for(int k=j+1;k<w;k++){
	  if(g[i][k] == '.')cnt++;
	  if(g[i][k] == '#')break;
	}
	if(cnt==0){
	  l[i][j] = 0; r[i][j] = w;
	  for(int k=j-1;k>=0;k--){
	    if(g[i][k] == '#'){
	      l[i][j] = k+1;
	      break;
	    }
	  }
	  for(int k=j+1;k<w;k++){
	    if(g[i][k] == '#'){
	      r[i][j] = k;
	      break;
	    }
	  }
	  if(r[i][j]-l[i][j]<3)r[i][j] = l[i][j] = -1;
	}

	cnt = 0;
	for(int k=i+1;k<h;k++){
	  if(g[k][j] == '.')cnt++;
	  if(g[k][j] == '#')break;
	}
	if(cnt==0){
	  u[i][j] = 0; d[i][j] = h;
