bool error;                      // 

int calculate(const string& s, int a, int b)
{
    if(a > b){
        error = true;
        return 0;
    }

    int k = b;
    while(k >= a && s[k] != '*' && s[k] != '/')
        -- k;
    if(k >= a){
        int x = calculate(s, a, k-1);
        int y = calculate(s, k+1, b);
        if(s[k] == '*'){
            return x * y;
        }else{
            if(y == 0 || x % y != 0){
                error = true;
                return 0;
            }
            return x / y;
        }
    }

    k = b;
    while(k >= a && s[k] != '+' && s[k] != '-')
        -- k;
    if(k >= a){
        int x = calculate(s, a, k-1);
        int y = calculate(s, k+1, b);
        if(s[k] == '+')
            return x + y;
        else
            return x - y;
    }

    if(s[a] == '0'){
        error = true;
        return 0;
    }

    int ret = 0;
    for(int i=a; i<=b; ++i){
        ret *= 10;
        ret += s[i] - '0';
    }

    return ret;
}

bool check(const string& s)
{
    int k = s.find('=');
    if(k == string::npos)
        return false;

    error = false;
    bool ret = calculate(s, 0, k-1) == calculate(s, k+1, s.size()-1);
    return !error && ret;
}

bool solve(int x, bitset<10> bs)
{
    if(x == n)
        return true;

    for(int i=0; i<n; ++i){
        if(bs[i])
            continue;
        bs[i] = true;

        change[x] = p[i];
        bool ok = true;
        for(unsigned j=0; j<formula[x].size(); ++j){
            string t = formula[x][j];
            for(unsigned k=0; k<t.size(); ++k){
                if(isalpha(t[k]))
                    t[k] = change[t[k]-'a'];
            }
            if(!check(t))
                ok = false;
        }

        if(ok && solve(x+1, bs))
            return true;

        bs[i] = false;
    }

    return false;
}

