bool notValid( string s ) {
    int n = s.size();
    for ( int i = 0; i < n; ++ i ) {
        if ( s[i] == '=' ) {
            return ! ( i >= 1 && ( n - ( i + 1 ) ) >= 1 );
        }
    }
    return true;
}

void addExpression( int& si, int& sj, int i, int j, int dir, string& exp, VI& ids ) {
    if ( notValid( exp ) ) {
        ids.clear();
        exp.clear();
        return;
    }
    int eid = E_ID_cnt ++;
    E_to_D_cnt[eid] = 0;
    for ( VI::iterator it_i = ids.begin(); it_i != ids.end(); ++ it_i ) {
        D_to_E[*it_i].push_back( eid );
        E_to_D_cnt[eid] ++;
    }
    E_ID[eid] = PPP( PII( si, sj ), PII( dir, (int)exp.size() ) );

    // debug
    // cout << (eid) << ", " << dir << ", " << exp << ": " << toString( E_ID[eid] ) << endl;
    // for ( VI::iterator it_i = ids.begin(); it_i != ids.end(); ++ it_i ) {
    //    cout << "\t" << *it_i << ": " << toString( D_ID_rev[*it_i] ) << endl;
    //}

    si = i;
    sj = j + 1;
    ids.clear();
    exp.clear();
}

void getExpressions() {
    // 
    for ( int i = 0; i < h; ++ i ) {
        string exp;
        VI ids;
        int si = i;
        int sj = 0;
        for ( int j = 0; j < w; ++ j ) {
            char c = M[i][j];
            if ( c == '#' ) {
                if ( exp.empty() ) continue;
                addExpression( si, sj, i, j, 0, exp, ids );
                continue;
            } else if ( c == '.' ) {
                ids.push_back( D_ID[PII(i,j)] );
            }
            exp += c;
        }
        if ( exp.empty() ) continue;
        addExpression( si, sj, NONE, NONE, 0, exp, ids );
    }
    // 
    for ( int i = 0; i < w; ++ i ) {
        string exp;
        VI ids;
        int si = 0;
        int sj = i;
        for ( int j = 0; j < h; ++ j ) {
            char c = M[j][i];
            if ( c == '#' ) {
                if ( exp.empty() ) continue;
                addExpression( si, sj, j, i, 1, exp, ids );
                continue;
            } else if ( c == '.' ) {
                ids.push_back( D_ID[PII(j,i)] );
            }
            exp += c;
        }
        if ( exp.empty() ) continue;
        addExpression( si, sj, NONE, NONE, 1, exp, ids );
    }
}

