using namespace std;
typedef long long LL;
typedef pair<int, LL> Result;
class ParseError{};
struct Parser{
    Result expr(string s, int p){
        Result r = term(s, p);
        while(s[r.first] == '+' || s[r.first] == '-'){
            Result t = term(s, r.first + 1);
            if(s[r.first] == '+'){
                r.second += t.second;
            }else{
                r.second -= t.second;
            }
            r.first = t.first;
        }
        return r;
    }
    Result term(string s, int p){
        Result r = factor(s, p);
        while(s[r.first] == '*' || s[r.first] == '/'){
            Result t = factor(s, r.first + 1);
            if(s[r.first] == '*'){
                r.second *= t.second;
            }else{
                if(t.second == 0) throw ParseError();
                r.second /= t.second;
            }
            r.first = t.first;
        }
        return r;
    }
    Result factor(string s, int p){
        if(s[p] == '0'){
            if(isdigit(s[p + 1])) throw ParseError();
            return Result(p + 1, 0);
        }else if(isdigit(s[p])){
            LL num = 0;
            while(isdigit(s[p])){
                num = num * 10 + s[p++] - '0';
            }
            return Result(p, num);
        }else{
            throw ParseError();
        }
    }
};
LL parse(string s){
    LL res = Parser().expr(s, 0).second;
    //cout << s << " " << res << endl;
    return res;
}
char select_ch[10];
bool used[10];
int H, W, L;
int iter;
bool dfs(int k, vector<char>& vc, vector<string> check[10]){
    if(++iter > (1 << 12)) return false;
    if(k == L) return true;
    REP(i, vc.size()) if(!used[i]){
        select_ch[k] = vc[i];
        used[i] = true;
        for(auto& s : check[k]){
            string s1, s2;
            for(auto c : s) {
                if(islower(c)){
                    c = select_ch[ c - 'a' ];
                }
                if(c == '='){
                    if(s2.size() > 0) goto NEXT;
                    swap(s1, s2);
                }else{
                    s1 += c;
                }
            }
            try{
                if(parse(s1) != parse(s2)) goto NEXT;
            }catch(ParseError e){
                goto NEXT;
            }
        }
        if(dfs(k + 1, vc, check)) return true;
        NEXT:
        used[i] = false;
    }
    return false;
}
int main(){
    while(cin >> H >> W && H){
        vector<string> grid(H);
        REP(i, H) cin >> grid[i];

        vector<pair<int, int>> bp;
        REP(y, H) REP(x, W) if(grid[y][x] == '.') { bp.push_back(make_pair(x, y)); }
        REP(i, bp.size()) grid[ bp[i].second ][ bp[i].first ] = 'a' + i;

        cin >> L;
        vector<char> vc(L);
        for(auto& c : vc) cin >> c;
        sort(vc.begin(), vc.end());

        vector<string> check[10];

        REP(y, H) REP(x, W) if(grid[y][x] != '#'){
            if(y < H - 2 && (y == 0 || grid[y - 1][x] == '#') && grid[y + 1][x] != '#' && grid[y + 2][x] != '#'){
                string s;
                for(int dy = 0; y + dy < H && grid[y + dy][x] != '#'; dy++){
                    s += grid[y + dy][x];
                }
                int maxk = 0;
                for(auto c : s) if(islower(c)) maxk = max(maxk, c - 'a');
                check[maxk].push_back(s);
            }
            if(x < W - 2 && (x == 0 || grid[y][x - 1] == '#') && grid[y][x + 1] != '#' && grid[y][x + 2] != '#'){
                string s;
                for(int dx = 0; x + dx < W && grid[y][x + dx] != '#'; dx++){
                    s += grid[y][x + dx];
                }
                int maxk = 0;
                for(auto c : s) if(islower(c)) maxk = max(maxk, c - 'a');
                check[maxk].push_back(s);
            }
        }

        memset(used, 0, sizeof used);
        iter = 0;
        bool ans = dfs(0, vc, check);

        if(ans) cout << "Yes" << endl;
        else cout << "No" << endl;
    }
    return 0;
}

