typedef pair<const char *, int > parsed;
typedef pair<bool,int> bnfm;

parsed expr(const char *);
parsed term(const char *);
parsed fact(const char *);

bnfm Ex(int pos, const string &s);
bnfm N0(int pos, const string &s);

parsed expr(const char *p){
  parsed r = term(p);
  if( r.first == NULL ) return r; // parse error
  while( *(r.first)=='+' || *(r.first)=='-' ){
    const char *op = r.first;
    parsed l = term(op+1);
    if( l.first == NULL ) return l;

    if( *op=='+' ){
      r.first = l.first;
      r.second = r.second + l.second;
    }else{
      r.first = l.first;
      r.second = r.second - l.second;
    }
  }
  return r;
}

parsed term(const char *p){
  parsed r = fact(p);
  if( r.first == NULL ) return r;
  while( *(r.first)=='*' || *(r.first)=='/' ){
    const char *op = r.first;
    parsed l = fact(op+1);
    if( l.first == NULL ) return l;

    if( *op=='*' ){
      r.first = l.first;
      r.second = r.second * l.second;
    }else{
      r.first = l.first;
      if( l.second == 0 || r.second % l.second != 0 ) return parsed(NULL,-1);
      r.second = int(r.second / l.second);
    }
  }
  return r;
}

inline parsed fact(const char *p){
  if( *p=='(' ){
    // new expression
    parsed tp = expr(p+1);
    if( tp.first == NULL ) return tp;
    return parsed(tp.first+1, tp.second);
  }
  if( *p=='-' ){
    // negative fact
    parsed tp = fact(p+1);
    if( tp.first == NULL ) return tp;
    tp.second *= -1;
    return tp;
  }
  if( isdigit(*p) ){
    // number
    int num = *p - '0';
    while( isdigit(*(++p)) ){
      num *= 10;
      num += *p - '0';
    }
    return parsed(p,num);
  }else assert(0); return parsed(NULL,0);
}

inline bool Eq(const string &s){
  int pos = 0;
  bnfm r = Ex(pos,s);
  if( !r.first ) return false;
  pos = r.second;
  if( pos >= s.length() ) return false;
  if( s[pos] != '=' ) return false;
  ++pos;
  r = Ex(pos,s);
  if( !r.first ) return false;
  if( r.second < s.length() ) return false;
  return true;
}

bnfm Ex(int pos, const string &s){
  if( pos >= s.length() ) return bnfm(false,pos);
  bnfm r = N0(pos,s);
  if( !r.first ) return bnfm(false,r.second);
  pos = r.second;
  if( pos >= s.length() ) return bnfm(true,pos);
  while( s[ pos ] == '+' || s[ pos ] == '-' || s[ pos ] == '*' || s[ pos ] == '/' ){
    bnfm r = N0( pos+1, s );
    if( !r.first ) return bnfm(false,r.second);
    pos = r.second;
    if( pos >= s.length() ) break;
  }
  return bnfm(true,pos);
}

bnfm N0(int pos, const string &s){
  if( pos >= s.length() ) return bnfm( false,pos );
  if( !isdigit( s[pos] ) ) return bnfm(false,pos+1);
  if( s[pos] == '0' ){
    if( pos + 1 < s.length() ){
      if( isdigit( s[pos+1] ) ) return bnfm(false,pos+2);
    }
    return bnfm(true,pos+1);
  }else{
    while( isdigit(s[pos]) ){
      ++pos;
      if( pos >= s.length() ) break;
    }
    return bnfm(true,pos);
  }
}

bool Evaluate(const string &s){
  string s1,s2;
  int i,j;
  for(i = 0; i < s.length(); ++i){
    if( s[i] == '=' ) break;
    else s1 += s[i];
  }
  for(j=i+1; j < s.length(); ++j){
    s2 += s[j];
  }
  parsed p1 = expr(s1.c_str());
  parsed p2 = expr(s2.c_str());
  if( p1.first == NULL || p2.first == NULL ) return false;
  if( p1.second == p2.second ) return true;
  else return false;
}

