int parse_int(std::string &s, size_t &i) {
  assert(isdigit(s[i]));
  int res=s[i++]-'0';
  while (isdigit(s[i])) {
    res = res*10 + s[i++]-'0';
  }
  return res;
}

int parse(std::string &s, size_t &i, bool &succ) {
  if (i == s.length()) {
    succ = false;
    return -1;
  }

  if (s[i] == '+' || s[i] == '-') {
    succ = false;
    return -1;
  }

  int res=0;
  char sgn='+';
  while (i < s.length()) {
    assert(isdigit(s[i]));

    int cur=parse_int(s, i);
    while (true) {
      if (s[i] == '*') {
        cur *= parse_int(s, ++i);
      } else if (s[i] == '/') {
        int tmp=parse_int(s, ++i);
        if (cur % tmp != 0) {
          succ = false;
          return -1;
        }
        cur /= tmp;
      } else {
        break;
      }
    }

    if (sgn == '+') {
      res += cur;
    } else if (sgn == '-') {
      res -= cur;
    }

    if (i == s.length() || s[i] == '=')
      return res;

    sgn = s[i++];
  }
  return res;
}

