using namespace std;

struct invalid {};
typedef const char *Iterator;

int eval_factor(Iterator& it, const Iterator& last)
{
  if (it == last) {
    throw invalid();
  }
  if (*it < '0' || '9' < *it) {
    throw invalid();
  }
  int val = *it - '0';
  ++it;
  if (val == 0) {
    return val;
  }
  while (it != last && '0' <= *it && *it <= '9') {
    val = 10*val + (*it - '0');
    ++it;
  }
  return val;
}

int eval_term(Iterator& it, const Iterator& last)
{
  int val = eval_factor(it, last);
  while (it != last && (*it == '*' || *it == '/')) {
    const char op = *it;
    ++it;
    const int x = eval_factor(it, last);
    if (op == '*') {
      val *= x;
    } else {
      if (x == 0 || val % x != 0) {
        throw invalid();
      }
      val /= x;
    }
  }
  return val;
}

int eval_expr(Iterator& it, const Iterator& last)
{
  int val = eval_term(it, last);
  while (it != last && (*it == '+' || *it == '-')) {
    const char op = *it;
    ++it;
    const int x = eval_term(it, last);
    if (op == '+') {
      val += x;
    } else {
      val -= x;
    }
  }
  return val;
}

bool check(Iterator it, const Iterator& last) try
{
  const int l = eval_expr(it, last);
  if (it == last || *it != '=') {
    return false;
  }
  ++it;
  const int r = eval_expr(it, last);
  return it == last && l == r;
} catch (const invalid&) {
  return false;
}

static const int M = 15;

bool extract_expr(char (*dst)[M], pair<int,int> (*tbl)[2], const char rows[M][M], int H, int W, int& s)
{
  for (int i = 0; i < H; i++) {
    for (int j = 0; j < W; j++) {
      if (j == 0 || rows[i][j-1] == '#') {
        const int k = j;
        ++j;
        while (j < W && rows[i][j] != '#') {
          ++j;
        }
        if (j-k > 2) {
          for (int l = k; l < j; l++) {
            dst[s][l] = rows[i][l];
            if (rows[i][l] >= 'a') {
              pair<int,int> *t = tbl[rows[i][l]-'a'];
              if (t[0].first != -1) {
                t[1].first = s;
                t[1].second = l;
              } else {
                t[0].first = s;
                t[0].second = l;
              }
            }
          }
          dst[s][j-k] = '\0';
          ++s;
;       }
      }
    }
  }
  return true;
}

bool solve(char (*exprs)[M], int s, char *cards, int N, const pair<int,int> (*tbl)[2])
{
  do {
    for (int i = 0; i < N; i++) {
      if (tbl[i][0].first >= 0) {
        exprs[tbl[i][0].first][tbl[i][0].second] = cards[i];
      }
      if (tbl[i][1].first >= 0) {
        exprs[tbl[i][1].first][tbl[i][1].second] = cards[i];
      }
    }
    for (int i = 0; i < s; i++) {
      if (!check(exprs[i], exprs[i]+strlen(exprs[i]))) {
        goto FAIL;
      }
    }
    return true;
FAIL:
    ;
  } while (next_permutation(cards, cards+N));
  return false;
}

int main()
{
  int H, W;
  while (scanf("%d %d", &H, &W) != EOF && H != 0) {
    char rows[M][M], cols[M][M];
    int n = 0;
    for (int i = 0; i < H; i++) {
      scanf("%s", rows[i]);
      for (int j = 0; j < W; j++) {
        if (rows[i][j] == '.') {
          rows[i][j] = 'a' + n;
          ++n;
        }
        cols[j][i] = rows[i][j];
        cols[j][H] = '\0';
      }
    }
    int N;
    scanf("%d", &N);
    char cards[M];
    for (int i = 0; i < N; i++) {
      char buf[4];
      scanf("%s", buf);
      cards[i] = buf[0];
    }
    sort(cards, cards+N);
    cards[N] = '\0';

    static char exprs[100][M];
    static pair<int,int> tbl[10][2];
    for (int i = 0; i < 10; i++) {
      tbl[i][0].first = tbl[i][1].second = -1;
    }
    int s = 0;
    if (!extract_expr(exprs, tbl, rows, H, W, s)) {
      puts("No");
      continue;
    }
    if (!extract_expr(exprs, tbl, cols, W, H, s)) {
      puts("No");
      continue;
    }
    if (s == 0) {
      puts("Yes");
      continue;
    }
    if (solve(exprs, s, cards, N, tbl)) {
      puts("Yes");
    } else {
      puts("No");
    }
  }
  return 0;
}

