#define value first
#define p second
using namespace std;
typedef pair<int,int> result;

// {{{
const int OutOfIntExc = 1, NegIntExc = 2, NotEquationExc = 3,
      NotNumExc = 4, LackNumExc = 5, IterExceedExc = 6,
      IncorrectExc = 7;// }}}

//{{{ 
result equation(const string &s, int p = 0);
result expression(const string &s, int p = 0);
result factor(const string &s, int p = 0);
result term(const string &s, int p = 0);
// }}} 

result equation(const string &s, int p) { // {{{
  if (s[p] == '=') { throw NotEquationExc; }
  result r = expression(s, p);
  if (s[r.p] != '=') { throw NotEquationExc; }

  r.p++;
  result t = expression(s, r.p);
  t.value = (int)(r.value == t.value);

  return t;
} // }}} 

result expression(const string &s, int p) { // {{{
  result r = factor(s, p);
  while (s[r.p] == '+' or s[r.p] == '-') {
    result r_ = factor(s, r.p+1);
    r.value += (s[r.p] == '+' ? r_.value : - r_.value);
    if (r.value < 0) { throw NegIntExc; }
    r.p = r_.p;
  }
  return r;
} // }}}

result factor(const string &s, int p) { // {{{
  result r = term(s, p);
  while (s[r.p] == '*' or s[r.p] == '/') {
    result r_ = term(s, r.p+1);
    if (s[r.p] == '*') {
      r.value *= r_.value;
    } else {
      if (r_.value == 0 or r.value % r_.value != 0) {
        throw OutOfIntExc;
      }
      r.value /= r_.value;
    }
    r.p = r_.p;
  }
  return r;
} // }}}

result term(const string &s, int p) { // {{{
  if (not isdigit(s[p])) { throw LackNumExc; }
  int value = 0;
  bool flag = false;

  while (isdigit(s[p])) {
    if (flag and value == 0) { throw NotNumExc; }
    flag = true;
    value = value * 10 + (s[p++] - '0');
  }
  return result(value, p);
} // }}}

#define REP(i,n) for(int i=0;i<(int)(n);i++)
#define X first
#define Y second
int H, W, n;
const int HMAX = 200, WMAX = 200, NMAX = 200;
char table[HMAX][WMAX], block[NMAX];

typedef pair<int,int> P;
bool printed[HMAX][WMAX];
bool hbegin[HMAX][WMAX], vbegin[HMAX][WMAX];
P blanks[NMAX];
P vsolvables[NMAX], hsolvables[NMAX];

bool incorrect(string str) { return equation(str).value != 1; }

bool legal(int x, int y) { // {{{
  return 0 <= x and x < W and 0 <= y and y < H;
} // }}}

string getv(int x, int y) { // {{{
  string str;
  while (legal(x, y) and table[x][y] != '#') {
    str.push_back(table[x][y]);
    y++;
  }
  return str;
} // }}}

string geth(int x, int y) { // {{{
  string str;
  while (legal(x, y) and table[x][y] != '#') {
    str.push_back(table[x][y]);
    x++;
  }
  return str;
} // }}}

bool solve() { // {{{
  sort(block, block + n);
  do {
    try {
      REP(i, n) {
        P p = blanks[i];
        table[p.first][p.second] = block[i];

        P q = vsolvables[i];
        if (q.first != -1 and incorrect(getv(q.first, q.second)))
          throw IncorrectExc;

        q = hsolvables[i];
        if (incorrect(geth(q.first, q.second)))
          throw IncorrectExc;
      }
    } catch (int exc) {
      continue;
    }
  } while (next_permutation(block, block + n));
} // }}}

void init() { // {{{
  REP(y, H) REP(x, W) {
    printed[x][y] = false;
    hbegin[x][y] = false;
    vbegin[x][y] = false;
  }
} // }}}

P v_solvable(P p) { // {{{
  string v = getv(p.X, p.Y);
  if (v.find(".", 0) == string::npos) {
    int x = p.X, y = p.Y;
    while (legal(x, y) and table[x][y]) y--;
    return P(x, ++y);
  } else {
    return P(-1, -1);
  }
} // }}}

P h_solvable(P p) { // {{{
  string h = geth(p.X, p.Y);
  if (h.find(".", 0) == string::npos) {
    int x = p.X, y = p.Y;
    while (legal(x, y) and table[x][y]) x--;
    return P(--x, y);
  } else {
    return P(-1, -1);
  }
} // }}}

int main() {
  while (cin >> H >> W and H + W) {
    init();

    REP(y, H) { // {{{
      string row;
      cin >> row;
      REP(x, W) {
        table[x][y] = row[x];
        printed[x][y] = row[x] != '.';
      }
    } // }}}

    REP(y, H) REP(x, W) { // {{{
      if (table[x][y]!='#' and legal(x+1,y) and table[x+1][y]!='#'
          and (!legal(x-1,y) or table[x-1][y] == '#')) {
        hbegin[x][y] = true;
      }
      if (table[x][y]!='#' and legal(x,y+1) and table[x][y+1]!='#'
          and (!legal(x,y-1) or table[x][y-1] == '#')) {
        vbegin[x][y] = true;
      }
    } // }}}

    for (int i = 0, x = 0; x < W; x++) for (int y = 0; y < H; y++) {
      if (table[x][y] == '.') {
        blanks[i] = P(x, y);
        i++;
      }
    }

    cin >> n;
    REP(i, n) { cin >> block[i]; }

    for (int i = 0; i < n; i++) {
      vsolvables[i] = v_solvable(blanks[i]);
      hsolvables[i] = h_solvable(blanks[i]);
    }

    cout << (solve() ? "Yes" : "No") << endl;
  }
}

