bool isOperation( char c ) {
    return c < '0' || c > '9';
}

bool isNumber( char c ) {
    return c >= '0' && c <= '9';
}

//
M_S_I memo;
int eval( string s ) {
    if ( memo.count( s ) ) return memo[s];
    int n = s.size();
    // debug
    // cout << "@eval: " << s << endl;

    // 
    char OP[20];
    int OP_cnt = 0;
    for ( int i = 0; i < n; ++ i ) {
        if ( isOperation( s[i] ) ) {
            OP[OP_cnt ++] = s[i];
        }
    }

    int NUM[20] = {};
    int NUM_cnt = 0;
    int mul = 1;
    for ( int i = n - 1; i >= 0; -- i ) {
        if ( isOperation( s[i] ) ) {
            // 
            NUM_cnt ++;
            mul = 1;
        } else {
            NUM[NUM_cnt] += ( s[i] - '0' ) * mul;
            mul *= 10;
        }
    }
    NUM_cnt ++;
    reverse( NUM, NUM + NUM_cnt );

    // *, /

    for ( int i = 0; i < OP_cnt; ++ i ) {
        if ( OP[i] == '+' || OP[i] == '-' ) continue;
        if ( OP[i] == '*' ) {
            NUM[i] *= NUM[i+1];
            for ( int j = i + 1; j + 2 < NUM_cnt; ++ j ) {
                NUM[j] = NUM[j+2];
            }
            NUM_cnt -= 2;
            OP_cnt --;
        } else if ( OP[i] == '/' ) {
            if ( NUM[i+1] == 0 ) return memo[s] = NONE;
            if ( NUM[i] % NUM[i+1] != 0 ) return memo[s] = NONE;
            NUM[i] /= NUM[i+1];
            for ( int j = i + 1; j + 2 < NUM_cnt; ++ j ) {
                NUM[j] = NUM[j+2];
            }
            NUM_cnt -= 2;
            OP_cnt --;
        }
    }

    int res = NUM[0];
    for ( int i = 0; i < OP_cnt; ++ i ) {
        if ( OP[i] == '+' ) {
            res += NUM[i+1];
        } else {
            res -= NUM[i+1];
        }
    }

    // debug
    // cout << res << endl;

    return memo[s] = res;
}

bool check2( string& s ) {
    int n = s.size();
    if ( isOperation( s[n-1] ) ) return false;
    if ( s[0] != '-' && isOperation( s[0] ) ) return false;
    for ( int i = 0; i + 1 < n; ++ i ) {
        if ( isOperation( s[i] ) && isOperation( s[i + 1] ) ) return false;
    }
    bool flag = true;
    for ( int i = 0; i + 1 < n; ++ i ) {
        if ( flag ) {
            if ( s[i] == '0' && isNumber( s[i + 1] ) ) return false;
            flag = false;
        } else if ( isOperation( s[i] ) ) {
            flag = true;
        }
    }
    return true;
}

bool check( string& s ) {
    int n = s.size();
    string left, right;
    bool flag = true;
    for ( int i = 0; i < n; ++ i ) {
        if ( s[i] == '=' ) {
            flag = false;
        } else if ( flag ) {
            left += s[i];
        } else {
            right += s[i];
        }
    }
    return ( check2( left ) && check2( right ) ) ? eval( left ) == eval( right ) : false;
}

bool solve() {
