                r += dr[info.second.first];
                c += dc[info.second.first];
            }
            if ( ! check( exp ) ) {
                // cout << exp << endl;
                flag = false;
                break;
            }
        }
        if ( flag ) return true;
    } while ( next_permutation( O.begin(), O.end() ) );
    return false;
}

void getID() {
