    for ( int i = 0; i < h; ++ i ) {
        string exp;
        VI ids;
        int si = i;
        int sj = 0;
        for ( int j = 0; j < w; ++ j ) {
            char c = M[i][j];
            if ( c == '#' ) {
                if ( exp.empty() ) continue;
                addExpression( si, sj, i, j, 0, exp, ids );
                continue;
            } else if ( c == '.' ) {
                ids.push_back( D_ID[PII(i,j)] );
            }
            exp += c;
        }
        if ( exp.empty() ) continue;
        addExpression( si, sj, NONE, NONE, 0, exp, ids );
    }
    // 
    for ( int i = 0; i < w; ++ i ) {
        string exp;
        VI ids;
        int si = 0;
        int sj = i;
        for ( int j = 0; j < h; ++ j ) {
            char c = M[j][i];
            if ( c == '#' ) {
                if ( exp.empty() ) continue;
                addExpression( si, sj, j, i, 1, exp, ids );
                continue;
            } else if ( c == '.' ) {
                ids.push_back( D_ID[PII(j,i)] );
            }
            exp += c;
        }
        if ( exp.empty() ) continue;
        addExpression( si, sj, NONE, NONE, 1, exp, ids );
    }
}

