tuple<int,int> number(const string& s,int i)
{
	int x=0;
	if(i>=s.size() || !isdigit(s[i])) return mt(INF,-1);
	for(int len=0;i<s.size() && isdigit(s[i]);i++,len++){
		if(len==1 && x==0) return mt(INF,-1);
		x=x*10+s[i]-'0';
	}
	return mt(x,i);
}
tuple<int,int> term(const string& s,int i)
{
	int x; tie(x,i)=number(s,i);
	if(i==-1) return mt(INF,-1);
	while(i<s.size() && (s[i]=='*' || s[i]=='/')){
		char op=s[i];
		int y; if(get<1>(tie(y,i)=number(s,i+1))==-1) return mt(INF,-1);
		if(op=='*') x*=y;
		if(op=='/'){
			if(y==0 || x%y>0) return mt(INF,-1);
			x/=y;
		}
	}
	return mt(x,i);
}
tuple<int,int> expr(const string& s,int i)
{
	int x; if(get<1>(tie(x,i)=term(s,i))==-1) return mt(INF,-1);
	while(i<s.size()){
		char op=s[i];
		if(op!='+' && op!='-') return mt(INF,-1);
		int y; if(get<1>(tie(y,i)=term(s,i+1))==-1) return mt(INF,-1);
		if(op=='+') x+=y;
		if(op=='-') x-=y;
	}
	return mt(x,i);
}

bool check(const vector<char*>& f)
{
	string s; for(char* p:f) s+=*p;
	if(count(all(s),'=')!=1) return false;
	int eq=s.find('=');
	if(eq==0 || eq==s.size()-1) return false;
	string a=s.substr(0,eq),b=s.substr(eq+1);
	auto x=get<0>(expr(a,0)),y=get<0>(expr(b,0));
	return x!=INF && y!=INF && x==y;
}

bool solve(int i,vs& grid,string& cs,const vi& is,const vi& js,const vector<vector<char*>>& fs,const vvi& ds)
{
	if(i==cs.size()) return true;

	for(char& c:cs) if(c!='X'){
		grid[is[i]][js[i]]=c; c='X';
		if(all_of(all(ds[i]),[&](int j){return check(fs[j]);}))
			if(solve(i+1,grid,cs,is,js,fs,ds))
				return true;
		c=grid[is[i]][js[i]];
	}
	return false;
}

