using namespace std;
typedef long long LL;
typedef pair<int, LL> Result;
class ParseError{};
struct Parser{
    Result expr(string s, int p){
        Result r = term(s, p);
        while(s[r.first] == '+' || s[r.first] == '-'){
            Result t = term(s, r.first + 1);
            if(s[r.first] == '+'){
                r.second += t.second;
            }else{
                r.second -= t.second;
            }
            r.first = t.first;
        }
        return r;
    }
    Result term(string s, int p){
        Result r = factor(s, p);
        while(s[r.first] == '*' || s[r.first] == '/'){
            Result t = factor(s, r.first + 1);
            if(s[r.first] == '*'){
                r.second *= t.second;
            }else{
                if(t.second == 0) throw ParseError();
                r.second /= t.second;
            }
            r.first = t.first;
        }
        return r;
    }
    Result factor(string s, int p){
        if(s[p] == '0'){
            if(isdigit(s[p + 1])) throw ParseError();
            return Result(p + 1, 0);
        }else if(isdigit(s[p])){
            LL num = 0;
            while(isdigit(s[p])){
                num = num * 10 + s[p++] - '0';
            }
            return Result(p, num);
        }else{
            throw ParseError();
        }
    }
};
LL parse(string s){
    LL res = Parser().expr(s, 0).second;
    //cout << s << " " << res << endl;
    return res;
}
