bool is_operand(char c) {
    return c == '=' or c == '+' or c == '-' or c == '*' or c == '/';
}

bool operand_connected(string str) {
  bool flag = true;
  for (int i = 0, len = str.size(); i < len; i++) {
    if (is_operand(str[i])) {
      if (flag) return true;
      flag = true;
    } else {
