								u.push (P (row, col ) );
							}else
							if (!equ ){
								exp += cur[row][col];
								used[row][col] = true;
								u.push (P (row, col ) );
								equ = true;
							}else{
								if (!evaluate_exp (exp ) )
									return false;
							} // end if
						}else{
							if (equ ){
								if (!evaluate_exp (exp ) )
									return false;
							}else{
								while (!u.empty() ){
									P c = u.top(); u.pop();
									used[c.first][c.second] = false;
								} // end while
							} // end if
						} // end if
					} // end for
					if (!exp.empty() ){
						if (equ ){
							if (!evaluate_exp (exp ) )
								return false;
						}else{
							while (!u.empty() ){
								P c = u.top(); u.pop();
								used[c.first][c.second] = false;
							} // end while
						} // end if
					} // end if
				} // end for
			} // end if
		} // end rep
	} // end rep

	return true;
}

