#include <cstdlib>
using namespace std;

typedef pair<int, int> P;

vector<P> V;
int H, W, N;
char M[10][10], T[10];
bool flag;

const int dx[] = {1,0,-1,0};
const int dy[] = {0,1,0,-1};

int getNum(string &s, int &pos) {
  string num = "";
  if(!isdigit(s[pos])) {
    pos = s.size();
    flag = true;
    return -1;
  }
  while(pos < s.size() && isdigit(s[pos])) {
    num += s[pos++];
  }
  if(num[0] == '0') {
    pos = s.size();
    flag = true;
    return -1;
  }
  return atoi(num.c_str());
}


int term(string &s, int &pos) {
  int x = getNum(s, pos);
  while(pos < s.size()) {
    if(s[pos] == '*') {
      ++pos;
      x *= getNum(s, pos);
    } else if(s[pos] == '/') {
      ++pos;
      int tmp = getNum(s, pos);
      if(tmp == 0 || x % tmp != 0) {
	flag = true;
	return -1;
      }
      x /= tmp;
    } else {
      break;
    }
  }
  return x;
}

int exp(string &s, int &pos) {
  int x = term(s, pos);
  while(pos < s.size()) {
    if(s[pos] == '+') {
      ++pos;
      x += term(s, pos);
    } else if(s[pos] == '-') {
      ++pos;
      x -= term(s, pos);
    }else {
      break;
    }
  }
  return x;
}

bool check(int x, int y, int dir) {
  string s[2];
  s[0] = s[1] = "";
  int p = 0;
  int a, b;
  do {
    if(M[y][x] == '=') {
      ++p;
    } else {
      s[p] += M[y][x];
    }
    x += dx[dir];
    y += dy[dir];
  } while(x < W && y < H);

  for(int i = 0; i < 2; ++i)
    if(!isdigit(s[i][0]) || !isdigit(s[i][(int)s[i].size()-1])) return false;

  int pos;
  flag = false;

  pos = 0;
  a = exp(s[0], pos);
  pos = 0;
  b = exp(s[1], pos);

  return !flag && a == b;
}

bool judge() {
  for(int i = 0; i < H; ++i) {
    for(int j = 0; j < W; ++j) {
      if((i == 0 || M[i-1][j] == '#') &&
	 M[i][j] != '#' && i+2 < H && M[i+1][j] != '#' && M[i+2][j] != '#') {
	if(!check(j, i, 1)) return false;
      }
      if((j == 0 || M[i][j-1] == '#') &&
	 M[i][j] != '#' && j+2 < W && M[i][j+1] != '#' && M[i][j+2] != '#') {
	if(!check(j, i, 0)) return false;
      }
    }
  }
  return true;
}

bool rec(int t) {
  if(t == N) {
    return judge();
  }

  for(int i = 0; i < N; ++i) {
    int x = V[i].first;
    int y = V[i].second;
    if(M[y][x] != '.') continue;
    int type = -1; // 0:Num, 1:Operator
    for(int i = 0; i < 4; ++i) {
      int nx = x + dx[i];
      int ny = y + dy[i];
      if(nx < 0 || nx >= W) continue;
      if(ny < 0 || ny >= H) continue;
      if(M[ny][nx] != '#' && M[ny][nx] != '.') {
	if(!isdigit(M[ny][nx])) {
	  type = 1;
	  break;
	}
      }
    }
    if(!isdigit(T[t]) && type == 1) continue;

    M[y][x] = T[t];
    if(rec(t+1)) return true;
    M[y][x] = '.';
  }
  return false;
}

int main() {
  while((cin >> H >> W) && (H || W)) {
    V.clear();
    for(int i = 0; i < H; ++i) {
      for(int j = 0; j < W; ++j) {
	cin >> M[i][j];
	if(M[i][j] == '.') V.push_back(P(j, i));
      }
    }

    cin >> N;
    for(int i = 0; i < N; ++i) cin >> T[i];

    if(rec(0)) cout << "Yes" << endl;
    else        cout << "No" << endl;
  }
  return 0;
}

#include <bits/stdc++.h>
using namespace std;

#define for_(i,a,b) for(int i=(a);i<(b);++i)

const int dx[4] = {-1,0,1,0};
const int dy[4] = {0,-1,0,1};

class Equation {
public:
	string A, B;
	Equation(string S) {
		int p = S.find("=");
		A = S.substr(0, p);
		B = S.substr(p+1);
		// cerr << A << " " << B << endl;
	}

	string convert(string S, string symbol_map) {
		for (char& c : S) if ('a' <= c && c <= 'z') c = symbol_map[c - 'a'];
		return S;
	}

	pair< int, bool > calculate(string S) {
		stack< char > stack_op;
		stack< int > stack_num;

		auto calc = [&]() {
			char op = stack_op.top(); stack_op.pop();
			int b = stack_num.top(); stack_num.pop();
			int a = stack_num.top(); stack_num.pop();
			//cerr << a << op << b << endl;
			if (op == '+') stack_num.push(a + b);
			if (op == '-') stack_num.push(a - b);
			if (op == '*') stack_num.push(a * b);
			if (op == '/') {
				if (b == 0) return false;
				if (a % b != 0) return false;
				stack_num.push(a / b);
			}
			return true;	
		};

		S += '#';
		int num = -1;
		for (char c : S) {
			if (isdigit(c)) {
				if (num == -1) num = 0;
				num = 10 * num + (c - '0');
			}
			else {
				stack_num.push(num);
				num = -1;
				while (!stack_op.empty() && (stack_op.top() == '*' || stack_op.top() == '/')) {
					if (!calc()) return pair< int, bool >(0, false);
				}
				if (c != '#') stack_op.push(c);
			}
		}

		while (!stack_op.empty()) calc();
		return pair< int, bool >(stack_num.top(), true);
	}

	bool valid(string S) {
		int len = S.size();
		bool num_flag = false, op_flag = false;
		for_(i,0,len) {
			char c = S[i];
			if (isdigit(c)) {
				if (!num_flag && c == '0' && i<len-1 && isdigit(S[i+1])) return false; // reading zero
				num_flag = true;
				op_flag = false;
			}
			else {
				if (!num_flag || op_flag) return false; // unary operation
				num_flag = false;
				op_flag = true;
			}
		}
		return num_flag;
	}

	bool evaluate(string symbol_map) {
		string X = convert(A, symbol_map);
		string Y = convert(B, symbol_map);
		if (!valid(X) || !valid(Y)) return false;
		pair< int, bool > pX = calculate(X), pY = calculate(Y);
		//cerr << X << " ? " << Y << " -> " << pX.first << " ? " << pY.first << endl;
		return  pX.second && pY.second && pX.first == pY.first;
	}
};

int H, W, n, buf[11][11];
string board[11];
char symbol[11];

vector< Equation > findEquations() {
	vector< Equation > ret;

	for_(d,0,2) {
		memset(buf, 0, sizeof(buf));
		for_(y,0,H) for_(x,0,W) {
			if (board[y][x] != '#' && buf[y][x] == 0) {
				string S = "";
				int cx = x, cy = y;
				while (cy < H && cx < W && board[cy][cx] != '#') {
					S += board[cy][cx];
					buf[cy][cx] = 1;
					if (d == 0) ++cy;
					else ++cx;
				}
				if (S.size() >= 3) {
					ret.push_back(Equation(S));
					//cerr << S << endl;
				}
			}
		}
	}

	return ret;
}

bool solve() {
	int a = 0;
	for_(y,0,H) {
		cin >> board[y];
		for_(x,0,W) {
			if (board[y][x] == '.') {
				board[y][x] = char('a' + a);
				++a;
			}
		}
	}

	cin >> n;
	for_(i,0,n) cin >> symbol[i];
	sort(symbol, symbol + n);

	vector< Equation > eqs = findEquations();

	//do {
	for_(REP,0,20000) {
		random_shuffle(symbol, symbol + n);
		string s_map = "";
		for_(i,0,n) s_map += symbol[i];
		//cerr << s_map << endl;
		bool flag = true;
		for (Equation eq : eqs) {
			flag &= eq.evaluate(s_map);
			if (!flag) break;
		}
		if (flag) return true;
	}
	//} while (next_permutation(symbol, symbol + n));

	return false;
}

int main() {
	while (cin >> H >> W, H) {
		puts(solve() ? "Yes" : "No");
	}
}

