  string s[2];
  s[0] = s[1] = "";
  int p = 0;
  int a, b;

  do {
    if(M[y][x] == '=') {
      ++p;
    } else {
      s[p] += M[y][x];
    }
    x += dx[dir];
    y += dy[dir];
  } while(x < W && y < H);
  for(int i = 0; i < 2; ++i)
    if(!isdigit(s[i][0]) || !isdigit(s[i][(int)s[i].size()-1])) return false;

  int pos;
  flag = false;

  pos = 0;
  a = exp(s[0], pos);
  pos = 0;
  b = exp(s[1], pos);

  return !flag && a == b;
}

bool judge() {
  for(int i = 0; i < H; ++i) {
    for(int j = 0; j < W; ++j) {
      if((j == 0 || M[i][j-1] == '#') &&
	 M[i][j] != '#' && j+2 < W && M[i][j+1] != '#' && M[i][j+2] != '#') {
	if(!check(j, i, 0)) return false;

      }
      if((i == 0 || M[i-1][j] == '#') &&
	 M[i][j] != '#' && i+2 < H && M[i+1][j] != '#' && M[i+2][j] != '#') {
	if(!check(j, i, 1)) return false;
      }
    }
  }
  return true;
}

bool rec(int t) {
  if(t == N) {
    return judge();
  }

  for(int i = 0; i < N; ++i) {
    int x = V[i].first;
    int y = V[i].second;
    if(M[y][x] != '.') continue;
    int type = -1; // 0:Num, 1:Operator
    for(int i = 0; i < 4; ++i) {
      int nx = x + dx[i];
      int ny = y + dy[i];
      if(nx < 0 || nx >= W) continue;
      if(ny < 0 || ny >= H) continue;
      if(M[ny][nx] != '#' && M[ny][nx] != '.') {
	if(!isdigit(M[ny][nx])) {
	  type = 1;
	  break;
	}
      }
    }
    if(!isdigit(T[t]) && type == 1) continue;

    M[y][x] = T[t];
    if(rec(t+1)) return true;
    M[y][x] = '.';
  }
  return false;
}

int main() {
  while((cin >> H >> W) && (H || W)) {
    V.clear();
    for(int i = 0; i < H; ++i) {
      for(int j = 0; j < W; ++j) {
	cin >> M[i][j];
	if(M[i][j] == '.') V.push_back(P(j, i));
      }
    }

    cin >> N;
    for(int i = 0; i < N; ++i) cin >> T[i];
    sort(T, T+N);
    if(rec(0)) cout << "Yes" << endl;
    else        cout << "No" << endl;
  }
  return 0;
}

#include <cstdio>
#include <algorithm>
using namespace std;

struct invalid {};
typedef const char *Iterator;

int eval_factor(Iterator& it, const Iterator& last)
{
  if (it == last) {
    throw invalid();
  }
  if (*it < '0' || '9' < *it) {
    throw invalid();
  }
  int val = *it - '0';
  ++it;
  if (val == 0) {
    return val;
  }
  while (it != last && '0' <= *it && *it <= '9') {
    val = 10*val + (*it - '0');
    ++it;
  }
  return val;
}

int eval_term(Iterator& it, const Iterator& last)
{
  int val = eval_factor(it, last);
  while (it != last && (*it == '*' || *it == '/')) {
    const char op = *it;
    ++it;
    const int x = eval_factor(it, last);
    if (op == '*') {
      val *= x;
    } else {
      val /= x;
    }
  }
  return val;
}

int eval_expr(Iterator& it, const Iterator& last)
{
  int val = eval_term(it, last);
  while (it != last && (*it == '+' || *it == '-')) {
    const char op = *it;
    ++it;
    const int x = eval_term(it, last);
    if (op == '+') {
      val += x;
    } else {
      val -= x;
    }
  }
  return val;
}

bool check(Iterator it, const Iterator& last) try
{
  const int l = eval_expr(it, last);
  if (it == last || *it != '=') {
    return false;
  }
  ++it;
  const int r = eval_expr(it, last);
  return it == last && l == r;
} catch (const invalid&) {
  return false;
}

static const int M = 15;
bool valid(const char rows[M][M], int H, int W)
{
  for (int i = 0; i < H; i++) {
    for (int j = 0; j < W; j++) {
      if (rows[i][j] != '#') {
        if (j == 0 || rows[i][j-1] == '#') {
          const int k = j;
          ++j;
          while (j < W && rows[i][j] != '#') {
            ++j;
          }
          if (j-k > 2) {
            if (!check(rows[i]+k, rows[i]+j)) {
              return false;
            }
          }
        }
      }
    }
  }
  return true;
}

bool solve(char rows[M][M], char cols[M][M], int H, int W, const pair<int,int> *spaces, char *cards, int N)
{
  sort(cards, cards+N);
  do {
    for (int i = 0; i < N; i++) {
      rows[spaces[i].first][spaces[i].second] = cards[i];
      cols[spaces[i].second][spaces[i].first] = cards[i];
    }
    if (valid(rows, H, W) && valid(cols, W, H)) {
      return true;
    }
  } while (next_permutation(cards, cards+N));
  return false;
}

int main()
{
  int H, W;
  while (scanf("%d %d", &H, &W) != EOF && H != 0) {
    char rows[M][M], cols[M][M];
    pair<int,int> spaces[M];
    pair<int,int> *p = spaces;
    for (int i = 0; i < H; i++) {
      scanf("%s", rows[i]);
      for (int j = 0; j < W; j++) {
        cols[j][i] = rows[i][j];
        cols[j][H] = '\0';
        if (rows[i][j] == '.') {
          p->first = i;
          p->second = j;
          ++p;
        }
      }
    }
    int N;
    scanf("%d", &N);
    char cards[M];
    for (int i = 0; i < N; i++) {
      char buf[4];
      scanf("%s", buf);
      cards[i] = buf[0];
    }
    cards[N] = '\0';
    if (solve(rows, cols, H, W, spaces, cards, N)) {
      puts("Yes");
    } else {
      puts("No");
    }
  }
  return 0;
}

#include <algorithm>
#include <array>
#include <cctype>
#include <cstdlib>
#include <iostream>
#include <utility>
#include <vector>
#include <unordered_set>
using namespace std;

constexpr int MAX_SIZE = 10;
constexpr int MAX_N = 10;

int h, w;
array<string, MAX_SIZE> puzzle;

int n;
array<char, MAX_N> in;

array<char, 128> letters;
array<bool, 128> is_operator;
array<bool, MAX_N> not_operator;
array<bool, MAX_N> not_zero;
array<bool, MAX_N> not_minus;
array<unordered_set<int>, MAX_N> left_idx, right_idx;

int number(string::const_iterator &it) {
	int res = 0;
	while(isdigit(*it)) {
		res = res * 10 + (*it - '0');
		++it;
	}
	return res;
}

int factor(string::const_iterator &it) {
	if(isdigit(*it)) return number(it);
	throw "parse error";
}

int term(string::const_iterator &it) {
	int res = factor(it);
	while(true) {
		int tmp;
		switch(*it) {
		case '*':
			++it;
			res *= factor(it);
			break;
		case '/':
			++it;
			tmp = factor(it);
			if(tmp == 0) throw "division by zero";
			if(res % tmp) throw "division leaving a remainder";
			res /= tmp;
			break;
		default:
			return res;
		}
		++it;
	}
	return res;
}

int expr(string::const_iterator &it) {
	int res = term(it);
	while(true) {
		switch(*it) {
		case '+':
			++it;
			res += term(it);
			break;
		case '-':
			++it;
			res -= term(it);
			break;
		default:
			return res;
		}
	}
}

bool valid(const vector<int> &expression) {
	const int n = expression.size();
	string left = "", right = "";
	int i;

	for(i = 0; i < n; ++i) {
		if(expression[i] == '=') break;
		left += letters[expression[i]];
	}

	for(++i; i < n; ++i) {
		right += letters[expression[i]];
	}

	try {
		string::const_iterator l_it = left.cbegin(), r_it = right.cbegin();
		if(expr(l_it) != expr(r_it)) return false;
	}
	catch(const char *c) {
		return false;
	}
	return true;
}

bool calc(int &x, int &y, int dx, int dy) {
	while(0 <= x && x < w && 0 <= y && y < h) {
		if(puzzle[y][x] == '=') return false;
		if(puzzle[y][x] == '#') {
			x -= dx;
			y -= dy;
			return true;
		}
		x += dx;
		y += dy;
	}
	x -= dx;
	y -= dy;
	return true;
}

void update(const vector<int> &expression) {
	for(unsigned i = 0; i < expression.size(); ++i) {
		if(isdigit(expression[i])) {
			if(i && expression[i - 1] < MAX_SIZE) {
				not_minus[expression[i - 1]] = true;
				not_zero[expression[i - 1]] = true;
			}
		}
		else if(expression[i] < MAX_SIZE) { // space
			if(i) {
				if(expression[i - 1] < MAX_SIZE) {
					left_idx[expression[i]].insert(expression[i - 1]);
					right_idx[expression[i - 1]].insert(expression[i]);
				}
			}
			else {
				not_minus[expression[i]] = true;
			}
		}
		else { // operator
			if(expression[i - 1] < MAX_SIZE) {
				not_operator[expression[i - 1]] = true;
			}
			if(i + 1 < expression.size() && expression[i + 1] < MAX_SIZE) {
				not_operator[expression[i + 1]] = true;
			}
		}
	}
}

vector<pair<int, vector<int>>> construct() {
	vector<vector<int>> idx(h, vector<int>(w, -1));
	int next = 0;

	vector<pair<int, vector<int>>> expressions;
	expressions.reserve(40);
	for(int i = 0; i < h; ++i) {
		for(int j = 0; j < w; ++j) {
			if(puzzle[i][j] == '=') {
				// vertical
				int s = i - 1, g = i + 1;
				if(calc(j, s, 0, -1) && calc(j, g, 0, 1) && s != i && g != i) {
					vector<int> expression;
					expression.reserve(g - s + 1);
					int max_index = -1;
					for(int k = s; k <= g; ++k) {
						if(puzzle[k][j] == '.') {
							if(idx[k][j] == -1) idx[k][j] = next++;
							expression.emplace_back(idx[k][j]);
							max_index = max(max_index, idx[k][j]);
						}
						else {
							expression.emplace_back(puzzle[k][j]);
						}
					}

					if(max_index == -1) continue;
					update(expression);
					expressions.emplace_back(max_index, expression);
				}

				// horizontal
				s = j - 1; g = j + 1;
				if(calc(s, i, -1, 0) && calc(g, i, 1, 0) && s != j && g != j) {
					vector<int> expression;
					expression.reserve(g - s + 1);
					int max_index = -1;
					for(int k = s; k <= g; ++k) {
						if(puzzle[i][k] == '.') {
							if(idx[i][k] == -1) idx[i][k] = next++;
							expression.emplace_back(idx[i][k]);
							max_index = max(max_index, idx[i][k]);
						}
						else {
							expression.emplace_back(puzzle[i][k]);
						}
					}

					if(max_index == -1) continue;
					update(expression);
					expressions.emplace_back(max_index, expression);
				}
			}
		}
	}

	sort(expressions.begin(), expressions.end());
	return expressions;
}

bool dfs(const vector<pair<int, vector<int>>> &expressions, int next, int idx, int used) {
	while(next < expressions.size() && expressions[next].first < idx) {
		if(!valid(expressions[next].second)) return false;
		++next;
	}
	if(next == expressions.size()) return true;

	for(int i = 0; i < n; ++i) {
		if(used & (1 << i)) continue;
		const char c = in[i];
		if(is_operator[c] && not_operator[idx]) continue;
		if(c == '0' && not_zero[idx]) continue;
		if(c == '-' && not_minus[idx]) continue;

		letters[idx] = c;
		const auto t_no = not_operator;
		const auto t_nz = not_zero;

		if(is_operator[c]) {
			for(const auto &e : left_idx[idx]) not_operator[e] = true;
			for(const auto &e : right_idx[idx]) not_operator[e] = true;
		}
		else {
			for(const auto &e : left_idx[idx]) not_zero[e];
		}

		if(dfs(expressions, next, idx + 1, used | (1 << i))) return true;
		not_operator = t_no;
		not_zero = t_nz;
	}
	return false;
}

int main() {
	cin.tie(nullptr);
	ios::sync_with_stdio(false);

	is_operator.fill(false);
	is_operator['+'] = true;
	is_operator['-'] = true;
	is_operator['*'] = true;
	is_operator['/'] = true;
	iota(begin(letters), end(letters), 0);

	while(cin >> h >> w && h) {
		not_operator.fill(false);
		not_zero.fill(false);
		not_minus.fill(false);
		for(int i = 0; i < n; ++i) {
			left_idx[i].clear();
			right_idx[i].clear();
		}

		for(int i = 0; i < h; ++i) {
			cin >> puzzle[i];
		}

		cin >> n;
		for(int i = 0; i < n; ++i) {
			cin >> in[i];
		}
		random_shuffle(in.begin(), in.begin() + n);

		const auto expressions = construct();
		cout << (dfs(expressions, 0, 0, 0) ? "Yes" : "No") << endl;
	}

	return EXIT_SUCCESS;
}

#include <bits/stdc++.h>
#define REP(i,n) for(int i=0; i<(int)(n); ++i)

using namespace std;
typedef long long LL;
typedef pair<int, LL> Result;
class ParseError{};
struct Parser{
    Result expr(string s, int p){
        Result r = term(s, p);
        while(s[r.first] == '+' || s[r.first] == '-'){
            Result t = term(s, r.first + 1);
            if(s[r.first] == '+'){
                r.second += t.second;
            }else{
                r.second -= t.second;
            }
            r.first = t.first;
        }
        return r;
    }
    Result term(string s, int p){
        Result r = factor(s, p);
        while(s[r.first] == '*' || s[r.first] == '/'){
            Result t = factor(s, r.first + 1);
            if(s[r.first] == '*'){
                r.second *= t.second;
            }else{
                if(t.second == 0) throw ParseError();
                r.second /= t.second;
            }
            r.first = t.first;
        }
        return r;
    }
    Result factor(string s, int p){
        if(s[p] == '0'){
            if(isdigit(s[p + 1])) throw ParseError();
            return Result(p + 1, 0);
        }else if(isdigit(s[p])){
            LL num = 0;
            while(isdigit(s[p])){
                num = num * 10 + s[p++] - '0';
            }
            return Result(p, num);
        }else{
            throw ParseError();
        }
    }
};
LL parse(string s){
    LL res = Parser().expr(s, 0).second;
    //cout << s << " " << res << endl;
    return res;
}
char select_ch[10];
bool used[10];
int H, W, L;
bool dfs(int k, vector<char>& vc, vector<string> check[10]){
    if(k == L) return true;
    REP(i, vc.size()) if(!used[i]){
        select_ch[k] = vc[i];
        used[i] = true;
        for(auto s : check[k]){
            string s1, s2;
            for(auto c : s) {
                if(islower(c)){
                    c = select_ch[ c - 'a' ];
                }
                if(c == '='){
                    if(s2.size() > 0) goto NEXT;
                    swap(s1, s2);
                }else{
                    s1 += c;
                }
            }
            try{
                if(parse(s1) != parse(s2)) goto NEXT;
            }catch(ParseError e){
                goto NEXT;
            }
        }
        if(dfs(k + 1, vc, check)) return true;
        NEXT:
        used[i] = false;
    }
    return false;
}
