using namespace std;
#define INF (1<<24)

bool isNum(char ch){return ('0'<=ch&&ch<='9');}

struct calcStr{
  string s;
  int pos,len;
  bool flag;
  void init(){ pos=0;len=s.size(); flag=false; }

  int head2Num(){
    int res=0;

    if(pos+1<len&&s[pos]=='0'&&isNum(s[pos+1]))flag=true;

    while(1){
      if(pos==len)break;
      if('0'<=s[pos]&&s[pos]<='9'){
	res*=10;
	res+=(s[pos]-'0');
      }else break;
      pos++;
    }

    return res;
  }

  int getNum(){
    int res=head2Num(),num;
    while(1){
      if(pos==len||s[pos]=='+'||s[pos]=='-')break;
      char ch=s[pos++];
      num=head2Num();
      if(ch=='*'){
	res*=num;
      }
      if(ch=='/'){
	if(num!=0&&res%num>=0)res/=num;
	else flag=true;

      }
    }
    return res;
  }

  int getAns(){
    int res=getNum(),num;
    while(1){
      if(pos==len||s[pos]=='=')break;
      char ch=s[pos++];
      num=getNum();
      if(ch=='+')res+=num;
      else if(ch=='-')res-=num;
    }
    if(flag)return INF;
    else return res;
  }
};

