        O = VC(O_cnt);
        for ( int i = 0; i < O_cnt; ++ i ) {
            cin >> O[i];
        }
        sort( O.begin(), O.end() );

        getID();
        getExpressions();

        cout << ( solve() ? "Yes" : "No" ) << endl;
    }
    return 0;
}

