Int exp(int &i) {
  Int p = term(i);
  while(str[i]=='+' || str[i]=='-') {
    char c = str[i++];
    if (c == '+') {
      Int a = term(i);
      p += a;
    }
    else p -= term(i);
  }
  return p;
}

Int term(int &i) {
  Int p = fact(i);
  while(str[i]=='*' || str[i]=='/') {
    if (str[i++] == '*') {
      p *= fact(i);
    } else {
      int r = fact(i);
      if (r == 0) throw 0;
      if (p % r == 0) p /= r;
      else throw 0;
    }
  }
  return p;
}

Int fact(int &i) {
  Int p = 0;
  if (!isdigit(str[i])) throw 0;
  while(isdigit(str[i])) {
    p *= 10;
    p += str[i]-'0';
    i++;
  }
  return p;
}

Int parse() {
  int id = 0;
  Int res = exp(id);
  if (str[id]) throw 0;
  return res;
}

inline bool isoperator(char c) {
  return c=='+'||c=='-'||c=='*'||c=='/'||c=='=';
}

bool check(char *s) {
  try {
    int n = strlen(s);
    int pos = -1;
    REP(i,strlen(s)) if (s[i]=='=') pos = i;
    if (pos == -1 || pos == 0 || pos == n-1) return 0;
    for (int i=0; i<pos; ++i) str[i] = s[i]; str[pos] = 0;
    Int a = parse();
    for (int i=pos+1; i<n; ++i) str[i-pos-1] = s[i]; str[n-pos-1] = 0;
    Int b = parse();
    if (a == b) return 1;
    else return 0;
  } catch(int a) {
    return 0;
  }
}


