#include <bits/stdc++.h>
#define REP(i,n) for(int i=0; i<(int)(n); ++i)

using namespace std;
typedef long long LL;
typedef pair<int, LL> Result;
class ParseError{};
struct Parser{
    Result expr(string s, int p){
        Result r = term(s, p);
        while(s[r.first] == '+' || s[r.first] == '-'){
            Result t = term(s, r.first + 1);
            if(s[r.first] == '+'){
                r.second += t.second;
            }else{
                r.second -= t.second;
            }
            r.first = t.first;
        }
        return r;
    }
    Result term(string s, int p){
        Result r = factor(s, p);
        while(s[r.first] == '*' || s[r.first] == '/'){
            Result t = factor(s, r.first + 1);
            if(s[r.first] == '*'){
                r.second *= t.second;
            }else{
                if(t.second == 0) throw ParseError();
                r.second /= t.second;
            }
            r.first = t.first;
        }
        return r;
    }
    Result factor(string s, int p){
        if(s[p] == '0'){
            if(isdigit(s[p + 1])) throw ParseError();
            return Result(p + 1, 0);
        }else if(isdigit(s[p])){
            LL num = 0;
            while(isdigit(s[p])){
                num = num * 10 + s[p++] - '0';
            }
            return Result(p, num);
        }else{
            throw ParseError();
        }
    }
};
LL parse(string s){
    LL res = Parser().expr(s, 0).second;
    //cout << s << " " << res << endl;
    return res;
}
char select_ch[10];
bool used[10];
int H, W, L;
int iter;
bool dfs(int k, vector<char>& vc, vector<string> check[10]){
    if(++iter > (1 << 14)) return false;
    if(k == L) return true;
    REP(i, vc.size()) if(!used[i]){
        select_ch[k] = vc[i];
        used[i] = true;
        for(auto& s : check[k]){
            string s1, s2;
            for(auto c : s) {
                if(islower(c)){
                    c = select_ch[ c - 'a' ];
                }
                if(c == '='){
                    if(s2.size() > 0) goto NEXT;
                    swap(s1, s2);
                }else{
                    s1 += c;
                }
            }
            try{
                if(parse(s1) != parse(s2)) goto NEXT;
            }catch(ParseError e){
                goto NEXT;
            }
        }
        if(dfs(k + 1, vc, check)) return true;
        NEXT:
        used[i] = false;
    }
    return false;
}
