using namespace std;
#define REP(i,n) for(int i=0;i<(int)(n);i++)

const int VMAX = 100;
const int INF = 1e7;

struct edge { int to, cap, rev; };

vector<edge> G[VMAX];
bool used[VMAX];

void add_edge(int from, int to, int cap) {
  G[from].push_back((edge){to, cap, (int)G[to].size()});
  G[to].push_back((edge){from, cap, (int)G[from].size() - 1});
}

int dfs(int v, int t, int f) {
  if (v == t) return f;
  used[v] = true;

  for (int i = 0; i < G[v].size(); i++) {
    edge &e = G[v][i];
    if (!used[e.to] and e.cap > 0) {
      int d = dfs(e.to, t, min(f, e.cap));
      if (d > 0) {
        e.cap -= d;
        G[e.to][e.rev].cap += d;
        return d;
      }
    }
  }
  return 0;
}

int max_flow(int s, int t) {
  int flow = 0;

  while (true) {
    memset(used, 0, sizeof(used));
    int f = dfs(s, t, INF);
    if (f == 0) return flow;
    flow += f;
  }
}

const int NMAX = 60, MMAX = 600, LMAX = 52;

vector<int> coefficients[NMAX];
int from[MMAX], to[MMAX];

void init(int N) {
  REP(i, N) {
    coefficients[i].clear();
  }
}

void init_flow(int v) {
  REP(i, v) {
    G[i].clear();
  }
}

vector<string> split_by(char div, string str) {
  vector<string> xs;
  string x;
  for (char c : str) {
    if (c == div) {
      xs.push_back(x);
      x = "";
    } else {
      x.push_back(c);
    }
  }
  xs.push_back(x);
  return xs;
}

pair<int,int> factor2pair(string factor) {
  int a, n;
  pair<int,int> p;
  cout << "(" << factor << ":";
  auto index = factor.find('x', 0);
  if (index == string::npos) {
    istringstream is(factor);
    is >> a;
    p = pair<int,int>(a,0);
  } else if (factor.find('^', 0) == string::npos) {
    factor = factor.substr(0, factor.length() - 1);
    cout << "<<" << factor << ">>";
    istringstream is((factor.empty() ? "1" : factor));
    is >> a;
    p = pair<int,int>(a, 1);
  } else {
    string astr = factor.substr(0, index - 1),
           nstr = factor.substr(index + 2);
    cout << "<" << astr << " " << nstr << ">";
    istringstream is(astr);
    is >> a;
    is.clear();
    is.str(nstr);
    is >> n;
    p = pair<int,int>(a,n);
  }
    cout << p.first << "," << p.second << ")" << endl;
  return p;
}

vector<int> polynomial2vector(string poly) {
  vector<string> factors = split_by('+', poly);
  vector<int> ans(LMAX);
  for (string factor : factors) {
    pair<int,int> p = factor2pair(factor);
    ans[p.first] = p.second;
  }
  return ans;
}

string vector2polynomial(vector<int> poly) {
  string ans = "";
  for (int i = 0, len = poly.size(); i < len; i++) {
    if (! poly[i]) continue;
    ans = poly[i] + (i == 0 ? "":"x") 
      + (i <= 1 ? "":"^" + to_string(i))+ ans;
  }
  return (ans.empty() ? "0" : ans);
}

int get_coefficient(int m, int l) {
  return coefficients[m][l];
}

int main() {
  int N, M;
  while (cin >> N >> M and N) {
    init(N);
    REP(i, M) {
      int u, v;
      string poly;
      cin >> u >> v >> poly;

      from[i] = u;
      to[i] = v;
      coefficients[i] = polynomial2vector(poly);
    }

    int lmax;
    vector<int> ans;

    REP(l, lmax) {
      init_flow(N);
      REP(m, M) {
        add_edge(from[m], to[m], get_coefficient(m, l));
        add_edge(to[m], from[m], get_coefficient(m, l));
      }
      ans.push_back(max_flow(1, N));
    }

    reverse(ans.begin(), ans.end());

    cout << vector2polynomial(ans) << endl;
  }
}

