		int a=eval(expr,s,p),b=eval(expr,p+1,t);
		if(a==inf||b==inf)return inf;
		return expr[p]=='+'?a+b:a-b;
	}
	p=t-1;
	for(;p>=s;p--)if(expr[p]=='*'||expr[p]=='/')break;
	if(p>=s)
	{
		int a=eval(expr,s,p),b=eval(expr,p+1,t);
		if(a==inf||b==inf)return inf;
		if(expr[p]=='/'&&(b==0||a%b!=0))return inf;
		return expr[p]=='*'?a*b:a/b;
	}

	int ret=0;
	for(p=t-1;p>=s;p--)ret*=10,ret+=expr[p]-'0';
	return ret;
}
bool ck2(bool tate,int y,int b,int x)
{
	char lhs[11]={0},rhs[11]={0}; int l=0,r=0;
	if(tate)
