			lhs[l++]=bd[y][b];
		}
		b++;
		for(;b<x;b++)rhs[r++]=bd[y][b];
	}

	int a=eval(lhs,0,l); b=eval(rhs,0,r);
	return a!=inf&&b!=inf&&a==b;
}
