#include <cstdio>
#include <cstring>
#include <algorithm>
using namespace std;

struct invalid {};
typedef const char *Iterator;

int eval_factor(Iterator& it, const Iterator& last)
{
  if (it == last) {
    throw invalid();
  }
  if (*it < '0' || '9' < *it) {
    throw invalid();
  }
  int val = *it - '0';
  ++it;
  if (val == 0) {
    return val;
  }
  while (it != last && '0' <= *it && *it <= '9') {
    val = 10*val + (*it - '0');
    ++it;
  }
  return val;
}

int eval_term(Iterator& it, const Iterator& last)
{
  int val = eval_factor(it, last);
  while (it != last && (*it == '*' || *it == '/')) {
    const char op = *it;
    ++it;
    const int x = eval_factor(it, last);
    if (op == '*') {
      val *= x;
    } else {
      val /= x;
    }
  }
  return val;
}

int eval_expr(Iterator& it, const Iterator& last)
{
  int val = eval_term(it, last);
  while (it != last && (*it == '+' || *it == '-')) {
    const char op = *it;
    ++it;
    const int x = eval_term(it, last);
    if (op == '+') {
      val += x;
    } else {
      val -= x;
    }
  }
  return val;
}

bool check(Iterator it, const Iterator& last) try
{
  const int l = eval_expr(it, last);
  if (it == last || *it != '=') {
    return false;
  }
  ++it;
  const int r = eval_expr(it, last);
  return it == last && l == r;
} catch (const invalid&) {
  return false;
}

static const int M = 15;

bool extract_expr(char (*dst)[M], pair<int,int> (*tbl)[2], const char rows[M][M], int H, int W, int& s)
{
  for (int i = 0; i < H; i++) {
    for (int j = 0; j < W; j++) {
      if (j == 0 || rows[i][j-1] == '#') {
        const int k = j;
        ++j;
        while (j < W && rows[i][j] != '#') {
          ++j;
        }
        if (j-k > 2) {
          bool b = false;
          for (int l = k; l < j; l++) {
            dst[s][l] = rows[i][l];
            if (rows[i][l] >= 'a') {
              b = true;
              pair<int,int> *t = tbl[rows[i][l]-'a'];
              if (t[0].first != -1) {
                t[1].first = s;
                t[1].second = l;
              } else {
                t[0].first = s;
                t[0].second = l;
              }
            }
          }
          dst[s][j-k] = '\0';
          ++s;
          if (!b && !check(rows[i]+k, rows[i]+j)) {
            return false;
          }
        }
      }
    }
  }
  return true;
}

bool solve(char (*exprs)[M], int s, char *cards, int N, const pair<int,int> (*tbl)[2])
{
  do {
    for (int i = 0; i < N; i++) {
      if (tbl[i][0].first >= 0) {
