int calc(string s)
{
  istringstream iss( s );
  vector<int> num;
  vector<char> op;
  int n;
  char c;
  while( iss >> n ){    
    num.push_back( n );
    if( iss >> c )op.push_back( c );
    else break;
  }

  for(int i=0; i<op.size(); ++i){
    if( op[i] == '+' || op[i] == '-' ) continue;
    else if( op[i] == '*' ) num[i] *= num[i+1];
    else if( op[i] == '/' ) num[i] /= num[i+1];
    else assert( false && "invalid op" );
    num.erase( num.begin() + i + 1);
    op.erase( op.begin() + i );
    --i;
  }

  int ret = num[0];
  for(int i=0; i<op.size(); ++i){
    if( op[i] == '+' )ret += num[i+1];
    if( op[i] == '-' )ret -= num[i+1];     
  }

  return ret;
}

inline bool is_op(char c){ return !isdigit(c); }

bool check_syntax(string s)
{
  if( count( s.begin(), s.end(), '=' ) != 1 )return false;
  string t[2];
  for(int i = 0, j=0; i<s.size(); ++i){
    if( s[i] == '=' ){ ++j; continue; }
    t[j] += s[i];
  }

  if( t[0].size() == 0 )return false;
  if( t[1].size() == 0 )return false;

  for(int i=0; i<2; ++i){
    for(int j=0; j<t[i].size(); ++j){
      if( !is_op(t[i][j]) ) continue;
      if( j == 0 || j+1 == t[i].size() )return false;
      if( is_op( t[i][j-1] ) || is_op( t[i][j+1] ) )return false;
    }
  }

  return calc(t[0]) == calc(t[1]);
}


struct F{
  vector< pair<int, int> > v;
  F(){ v.clear(); }
};

string build(F f)
{
  string s;
  for(int i=0; i<f.v.size(); ++i){
    s += g[ f.v[i].first ][ f.v[i].second ];
  }
  return s;
}

